import React from "react";
import {
  Editor,
  EditorState,
  RichUtils,
  convertToRaw,
  convertFromRaw,
} from "draft-js";

const styleMap = {
  CODE: {
    backgroundColor: "rgba(0, 0, 0, 0.05)",
    fontFamily: '"Inconsolata", "Menlo", "Consolas", monospace',
    fontSize: 16,
    padding: 4,
  },
  BOLD: {
    color: "#395296",
    fontWeight: "bold",
  },
  ANYCUSTOMSTYLE: {
    color: "#00e400",
  },
};

const CustomEditor = () => {
  const [state, setState] = React.useState({
    editorState: EditorState.createWithContent(convertFromRaw(initialData)),
    showToolbar: false,
    windowWidth: 0,
    toolbarMeasures: {
      w: 0,
      h: 0,
    },
    selectionMeasures: {
      w: 0,
      h: 0,
    },
    selectionCoordinates: {
      x: 0,
      y: 0,
    },
    toolbarCoordinates: {
      x: 0,
      y: 0,
    },
    showRawData: false,
  });

  const onClickEditor = () => {
    focus();
    checkSelectedText();
  };

  const checkSelectedText = () => {
    if (typeof window !== "undefined") {
      const text = window.getSelection().toString();
      if (text !== "") {
        // 1-a Define the selection coordinates
        setSelectionXY();
      } else {
        // Hide the toolbar if nothing is selected
        setState({
          ...state,
          showToolbar: false,
        });
      }
    }
  };

  const setSelectionXY = () => {
    var r = window.getSelection().getRangeAt(0).getBoundingClientRect();
    var relative = document.body.parentNode.getBoundingClientRect();
    // 2-a Set the selection coordinates in the state
    setState(
      {
        ...state,
        selectionCoordinates: r,
        windowWidth: relative.width,
        selectionMeasures: {
          w: r.width,
          h: r.height,
        },
      },
      () => showToolbar()
    );
  };

  const showToolbar = () => {
    setState(
      {
        ...state,
        showToolbar: true,
      },
      () => measureToolbar()
    );
  };

  const measureToolbar = () => {
    // 4-a Define the toolbar width and height, as it is now visible
    setState(
      {
        ...state,
        toolbarMeasures: {
          w: elemWidth,
          h: elemHeight,
        },
      },
      () => setToolbarXY()
    );
  };

  const setToolbarXY = () => {
    let coordinates = {};

    const {
      selectionMeasures,
      selectionCoordinates,
      toolbarMeasures,
      windowWidth,
    } = state;

    const hiddenTop = selectionCoordinates.y < toolbarMeasures.h;
    const hiddenRight =
      windowWidth - selectionCoordinates.x < toolbarMeasures.w / 2;
    const hiddenLeft = selectionCoordinates.x < toolbarMeasures.w / 2;

    const normalX =
      selectionCoordinates.x - toolbarMeasures.w / 2 + selectionMeasures.w / 2;
    const normalY = selectionCoordinates.y - toolbarMeasures.h;

    const invertedY = selectionCoordinates.y + selectionMeasures.h;
    const moveXToLeft = windowWidth - toolbarMeasures.w;
    const moveXToRight = 0;

    coordinates = {
      x: normalX,
      y: normalY,
    };

    if (hiddenTop) {
      coordinates.y = invertedY;
    }

    if (hiddenRight) {
      coordinates.x = moveXToLeft;
    }

    if (hiddenLeft) {
      coordinates.x = moveXToRight;
    }

    setState({
      ...state,
      toolbarCoordinates: coordinates,
    });
  };

  const toggleToolbar = (inlineStyle) => {
    onChange(RichUtils.toggleInlineStyle(state.editorState, inlineStyle));
  };

  const { editorState } = state;

  React.useEffect(() => {
    if (typeof window !== "undefined") {
      window.addEventListener("resize", checkSelectedText);
    }
  }, [window]);

  const toolbarStyle = {
    display: state.showToolbar ? "block" : "none",
    backgroundColor: "black",
    color: "white",
    position: "absolute",
    left: state.toolbarCoordinates.x,
    top: state.toolbarCoordinates.y,
    zIndex: 999,
    padding: 10,
  };

  const handleKeyCommand = (command) => {
    const { editorState } = state;
    const newState = RichUtils.handleKeyCommand(editorState, command);
    if (newState) {
      onChange(newState);
      return true;
    }
    return false;
  };

  return (
    <div>
      <div
        ref={(elem) => {
          elemWidth = elem ? elem.clientWidth : 0;
          elemHeight = elem ? elem.clientHeight : 0;
        }}
        style={toolbarStyle}
      >
        <ToolBar editorState={editorState} onToggle={toggleToolbar} />
      </div>
      <div onClick={onClickEditor} onBlur={checkSelectedText}>
        <Editor
          customStyleMap={styleMap}
          editorState={editorState}
          handleKeyCommand={handleKeyCommand}
          onChange={onChange}
          placeholder="Tell a story..."
          editorKey="foobar"
          spellCheck={false}
          ref={(element) => {
            editor = element;
          }}
        />
      </div>
      <div style={{ marginTop: 40 }}>
        <button
          onClick={() =>
            setState({ ...state, showRawData: !state.showRawData })
          }
        >
          {!state.showRawData ? "Show" : "Hide"} Raw Data
        </button>
        <br />
        {state.showRawData &&
          JSON.stringify(convertToRaw(editorState.getCurrentContent()))}
      </div>
    </div>
  );
};

const ToolbarButton = (props) => {
  const onToggle = (e) => {
    e.preventDefault();
    props.onToggle(props.style);
  };

  const [buttonStyle] = React.useState({ padding: 10 });

  return (
    <span onMouseDown={onToggle} style={buttonStyle}>
      {props.label}
    </span>
  );
};

const toolbarItems = [
  { label: "Bold", style: "BOLD" },
  { label: "Italic", style: "ITALIC" },
  { label: "Underline", style: "UNDERLINE" },
  { label: "Code", style: "CODE" },
  { label: "Surprise", style: "ANYCUSTOMSTYLE" },
];

const ToolBar = (props) => {
  const currentStyle = props.editorState.getCurrentInlineStyle();
  return (
    <div>
      {toolbarItems.map((toolbarItem) => (
        <ToolbarButton
          key={toolbarItem.label}
          active={currentStyle.has(toolbarItem.style)}
          label={toolbarItem.label}
          onToggle={props.onToggle}
          style={toolbarItem.style}
        />
      ))}
    </div>
  );
};

const initialData = {
  blocks: [
    {
      key: "16d0k",
      text: "You can edit this text.",
      type: "unstyled",
      depth: 0,
      inlineStyleRanges: [{ offset: 0, length: 23, style: "BOLD" }],
      entityRanges: [],
      data: {},
    },
    {
      key: "98peq",
      text: "",
      type: "unstyled",
      depth: 0,
      inlineStyleRanges: [],
      entityRanges: [],
      data: {},
    },
    {
      key: "ecmnc",
      text:
        "Luke Skywalker has vanished. In his absence, the sinister FIRST ORDER has risen from the ashes of the Empire and will not rest until Skywalker, the last Jedi, has been destroyed.",
      type: "unstyled",
      depth: 0,
      inlineStyleRanges: [
        { offset: 0, length: 14, style: "BOLD" },
        { offset: 133, length: 9, style: "BOLD" },
      ],
      entityRanges: [],
      data: {},
    },
    {
      key: "fe2gn",
      text: "",
      type: "unstyled",
      depth: 0,
      inlineStyleRanges: [],
      entityRanges: [],
      data: {},
    },
    {
      key: "4481k",
      text:
        "With the support of the REPUBLIC, General Leia Organa leads a brave RESISTANCE. She is desperate to find her brother Luke and gain his help in restoring peace and justice to the galaxy.",
      type: "unstyled",
      depth: 0,
      inlineStyleRanges: [
        { offset: 34, length: 19, style: "BOLD" },
        { offset: 117, length: 4, style: "BOLD" },
        { offset: 68, length: 10, style: "ANYCUSTOMSTYLE" },
      ],
      entityRanges: [],
      data: {},
    },
  ],
  entityMap: {},
};

export default CustomEditor;
