import { Col, Tooltip } from "antd";

const SelectGroup = ({ xs, lg, sm, md, name, options, info }) => {
  return (
    <Col xs={xs} sm={sm} md={md} lg={lg}>
      <div className="input-group">
        <label htmlFor="genre">
          {name}{" "}
          {info && (
            <Tooltip title={info}>
              <ion-icon name="information-circle"></ion-icon>
            </Tooltip>
          )}
        </label>
        <select className="form-input">
          {options
            ?.sort((a, b) => {
              const keyA = a.name,
                keyB = b.name;
              // Compare the 2 dates
              if (keyA < keyB) return -1;
              if (keyA > keyB) return 1;
              return 0;
            })
            .map((option) => (
              <option
                style={{ textTransform: "capitalize" }}
                key={option.value}
                value={option.value}
              >
                {option.name}
              </option>
            ))}
        </select>
      </div>
    </Col>
  );
};

export default SelectGroup;
