import React from "react";
import { Row, Col, Divider, Form, Input } from "antd";

import Relatives from "./Relatives";
import ImageRow from "./ImageRow";

const CharacterForm = ({ form, characters, character, submit, onFailed }) => {
  return (
    <Form
      layout="vertical"
      scrollToFirstError
      onFinishFailed={onFailed}
      onFinish={submit}
      form={form}
      initialValues={{ firstname: "" }}
    >
      <Row align="bottom" gutter={[24, 24]}>
        <Col md={8} xs={24} sm={24}>
          <Form.Item label="Firstname" name="firstname">
            <Input />
          </Form.Item>
        </Col>
        <Col md={8} xs={24} sm={24}>
          <Form.Item label="Lastname" name="lastname">
            <Input />
          </Form.Item>
        </Col>
        <Col md={4} xs={24} sm={24}>
          <div className="input-group">
            <label htmlFor="age">Age</label>
            <input id="age" type="number" className="form-input" />
          </div>
        </Col>
      </Row>
      <div className="input-group">
        <label htmlFor="description">Description</label>
        <textarea
          name="description"
          id="description"
          className="form-input"
        ></textarea>
      </div>
      <Relatives />
      <Divider />
      <ImageRow form={form} itemImage={character?.image} />
      <button type="submit">Create</button>
    </Form>
  );
};

export default CharacterForm;
