import React from "react";
import Link from "next/link";
import { Row, Col, Space, Empty, Tooltip } from "antd";

const Notifications = ({ notif }) => {
  return (
    <div className="notifications">
      <div className="cta-btns">
        <div className="cta-btn read-all" onClick={notif.markAllAsRead}>
          Mark all as seen
        </div>
        <div className="cta-btn delete-all" onClick={notif.deleteAll}>
          Delete all
        </div>
      </div>
      {notif.items.length > 0 ? (
        notif.items.map((n) => (
          <div
            className={`notification ${n.read ? "notif-read" : ""}`}
            key={n.id}
          >
            <Row align="middle" gutter={60} justify="space-between">
              <Col span={16}>
                <Link href="/story/1">
                  <a className="notification-desc">
                    <Space size={16}>
                      <div className="notification-avatar">
                        <img src={n.user.image} alt={n.user.username} />
                      </div>
                      <p>{n.message}</p>
                    </Space>
                  </a>
                </Link>
              </Col>

              <Col span={8}>
                <div className="notifications-buttons">
                  <Space size={20}>
                    <span className="btn mark-read-btn">
                      {n.read ? (
                        <ion-icon name="checkmark"></ion-icon>
                      ) : (
                        <span
                          style={{ cursor: "pointer" }}
                          onClick={() => notif.markAsRead(n.id)}
                        >
                          Mark as Read
                        </span>
                      )}
                    </span>
                    <Tooltip title="DELETE">
                      <span
                        onClick={() => notif.deleteOne(n.id)}
                        className="btn delete-btn"
                        style={{ cursor: "pointer" }}
                      >
                        <ion-icon name="trash-outline"></ion-icon>
                      </span>
                    </Tooltip>
                  </Space>
                </div>
              </Col>
            </Row>
          </div>
        ))
      ) : (
        <Empty
          image={Empty.PRESENTED_IMAGE_SIMPLE}
          description="No notifications yet"
        />
      )}
    </div>
  );
};

export default Notifications;
