import React from "react";
import { Pagination as Paginate, Divider } from "antd";

import { CharacterGrid, StoriesGrid } from "./Grid";

const Pagination = ({
  data,
  xs,
  sm,
  lg,
  xl,
  md,
  type,
  gutter,
  columnsCountBreakPoints,
  itemType,
}) => {
  const [pagination, setPagination] = React.useState({
    currentPage: 1,
    datasPerPage: 12,
  });
  const { currentPage, datasPerPage } = pagination;
  const indexOfLastData = currentPage * datasPerPage;
  const indexOfFirstData = indexOfLastData - datasPerPage;
  console.log(pagination, currentPage, datasPerPage);

  React.useState(() => {}, [data]);

  const currentDatas = data.slice(indexOfFirstData, indexOfLastData);

  const handleClick = (e) =>
    setPagination({ ...pagination, currentPage: Number(e) });

  const renderDatas =
    itemType === "stories" ? (
      <StoriesGrid
        gutter={gutter}
        type={type}
        stories={currentDatas}
        columnsCountBreakPoints={columnsCountBreakPoints}
      />
    ) : itemType === "characters" ? (
      <CharacterGrid
        columnsCountBreakPoints={columnsCountBreakPoints}
        gutter={gutter}
        type={type}
        characters={currentDatas}
      />
    ) : null;

  let pageNumbers = [];
  for (let i = 1; i <= Math.ceil(data.length / datasPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <React.Fragment>
      <Paginate
        size="small"
        showQuickJumper
        onChange={handleClick}
        total={data.length}
        pageSize={datasPerPage}
        showTotal={(total, range) => {
          console.log(range[0]);
          return `${range[0]}-${range[1]} of ${total} items`;
        }}
      />
      <Divider />
      {renderDatas}
      <Divider />
      <Paginate
        size="small"
        showQuickJumper
        onChange={handleClick}
        total={data.length}
        pageSize={datasPerPage}
        showTotal={(total, range) =>
          `${range[0]}-${range[1]} of ${total} items`
        }
      />
    </React.Fragment>
  );
};

export default Pagination;
