import React from "react";
import { connect } from "react-redux";
import { Result, Button, Form } from "antd";
import BraftEditor from "braft-editor";
import "braft-editor/dist/index.css";

import { useRouter } from "next/router";
import { useAuth } from "../../../../hooks/userHooks";
import { getStory } from "../../../../redux/actions/storiesActions";
import { getUserCharacters } from "../../../../redux/actions/charactersActions";
import Loader from "../../../../components/common/Loader";
import ChapterForm from "../../../../components/forms/ChapterForm";

const NewChapter = (props) => {
  const router = useRouter();
  const auth = useAuth();
  const [form] = Form.useForm();
  const { characters, storyExists, loading, story } = props;

  const [editorState, setEditorState] = React.useState(null);

  React.useEffect(() => {
    if (auth.user) {
      props.getUserCharacters();
    }
  }, [auth]);

  React.useEffect(() => {
    props.getStory(router.query.id);
  }, []);

  const submit = (values) => {
    console.log(values);
  };

  const submitContent = async () => {
    // Pressing ctrl + s when the editor has focus will execute this method
    // Before the editor content is submitted to the server, you can directly call editorState.toHTML () to get the HTML content
    const htmlContent = editorState.toHTML();
    // const result = await saveEditorContent(htmlContent)
  };

  const handleEditorChange = (editorState) => {
    setEditorState(editorState);
  };

  return !loading ? (
    storyExists ? (
      story.authorId === auth.user.uid ? (
        <div className="custom-form">
          <div className="inner">
            <h2 className="side-heading">Add a new chapter</h2>
            <ChapterForm submit={submit} form={form} />
            <BraftEditor
              value={editorState}
              onChange={handleEditorChange}
              onSave={submitContent}
            />
          </div>
        </div>
      ) : (
        <Result
          status="403"
          title="403"
          subTitle="Sorry, you are not authorized to access this page."
          extra={<Button type="primary">Back Home</Button>}
        />
      )
    ) : (
      <Result
        status="404"
        title="404"
        subTitle="Sorry, the page you visited does not exist."
        extra={<Button type="primary">Back Home</Button>}
      />
    )
  ) : (
    <Loader />
  );
};

const mapStateToProps = (state) => ({
  characters: state.characters.userCharacters,
  storyExists: state.stories.storyExists,
  loading: state.stories.loading,
  story: state.stories.story,
});

export default connect(mapStateToProps, {
  getUserCharacters,
  getStory,
})(NewChapter);
