import React from "react";

const Loader = (WrappedComponent, loading) => {
  const LoadingScreen = (props) => {
    return (
      <>
        {loading ? (
          <div className="loader-container">
            <div className="loader">
              <div></div>
              <div></div>
              <div></div>
            </div>
          </div>
        ) : (
          <WrappedComponent {...props} />
        )}
      </>
    );
  };
  return LoadingScreen;
};

export default Loader;
