import React from "react";
import { Col, Select, Radio, Form, Divider } from "antd";

import { CATEGORIES, LANGUAGES, STATUS } from "../../utils/constants";
import RadioButton from "../forms/RadioButton";

const Filter = ({
  setCurrentCategory,
  currentCategory,
  setCurrentStatus,
  currentStatus,
  setCurrentLanguage,
  currentLanguage,
  mature,
  setMature,
}) => {
  const radioStyle = {
    display: "block",
    height: "30px",
    lineHeight: "30px",
  };

  return (
    <Col span={6}>
      <aside className="filter">
        <h4>Filter</h4>
        <Form
          layout="vertical"
          initialValues={{
            mature: false,
            language: "",
            category: "",
            status: "",
          }}
        >
          <div>
            <Form.Item label="Categories" name="category">
              <Select
                value={currentCategory}
                onChange={(val) => setCurrentCategory(val)}
              >
                <Select.Option value="">Choose a category</Select.Option>
                {CATEGORIES.map((c) => (
                  <Select.Option key={c.value} value={c.value}>
                    {c.name}
                  </Select.Option>
                ))}
              </Select>
            </Form.Item>
          </div>
          <Divider />
          <div>
            <Form.Item label="Languages" name="language">
              <Select
                value={currentLanguage}
                onChange={(val) => setCurrentLanguage(val)}
              >
                <Select.Option value="">Choose a language</Select.Option>
                {LANGUAGES.map((l) => (
                  <Select.Option key={l.value} value={l.value}>
                    {l.name}
                  </Select.Option>
                ))}
              </Select>
            </Form.Item>
          </div>
          <Divider />
          <div>
            <Form.Item label="Status" name="status">
              <Radio.Group
                onChange={(e) => setCurrentStatus(e.target.value)}
                value={currentStatus}
              >
                {STATUS.map((s) => (
                  <Radio key={s.value} style={radioStyle} value={s.value}>
                    {s.name}
                  </Radio>
                ))}
              </Radio.Group>
            </Form.Item>
          </div>
          <Divider />
          <div>
            <RadioButton
              name={"mature"}
              label={"Mature Content ?"}
              num1={1}
              num2={2}
              label1="Yes"
              label2="No"
              setFunc={setMature}
              val={mature}
            />
            <Form.Item name="mature" label="Mature content ?">
              <Radio.Group
                onChange={(e) => setMature(e.target.value)}
                value={mature}
              >
                <Radio style={radioStyle} value={true}>
                  Yes
                </Radio>
                <Radio style={radioStyle} value={false}>
                  No
                </Radio>
              </Radio.Group>
            </Form.Item>
          </div>
        </Form>
      </aside>
    </Col>
  );
};

export default Filter;
