import React from "react";
import { connect } from "react-redux";
import { Result, Button, Form, Divider, Row, Col } from "antd";
import { createEditor } from "slate";
import { Slate, Editable, withReact } from "slate-react";

import { useRouter } from "next/router";
import { getUserCharacters } from "../../../../redux/actions/charactersActions";
import ChapterForm from "../../../../components/forms/ChapterForm";
import CharacterSelector from "../../../../components/forms/CharacterSelector";
import withAuthorization from "../../../../components/hoc/withAuthorization";

const NewChapter = (props) => {
  const [form] = Form.useForm();
  const { characters, storyExists, auth } = props;
  const editor = React.useMemo(() => withReact(createEditor()), []);

  const [value, setValue] = React.useState([
    {
      type: "paragraph",
      children: [{ text: "A line of text in a paragraph." }],
    },
  ]);

  const submit = (values) => {
    console.log(values);
  };

  return storyExists ? (
    <div className="custom-form">
      <div className="inner">
        <h2 className="side-heading">Add a new chapter</h2>
        <ChapterForm submit={submit} form={form} />
        {/* <Slate
              editor={editor}
              value={value}
              onChange={(newValue) => setValue(newValue)}
            >
              <Editable
                onKeyDown={(event) => {
                  if (event.key === "&") {
                    event.preventDefault();
                    // editor.insertText("and");
                  }
                }}
              />
            </Slate> */}
        <Divider />
        <Row gutter={24}>
          <Col md={12} xs={24} sm={24}>
            <CharacterSelector
              label={"Characters in this chapter"}
              arrInForm={"characters"}
              form={form}
              type="chapter"
              characters={characters}
              lg1={24}
            />
          </Col>
        </Row>
      </div>
    </div>
  ) : (
    <Result
      status="404"
      title="404"
      subTitle="Sorry, the page you visited does not exist."
      extra={<Button type="primary">Back Home</Button>}
    />
  );
};

const mapStateToProps = (state) => ({
  storyExists: state.stories.storyExists,
  loading: state.stories.loading,
});

export default connect(mapStateToProps, {
  getUserCharacters,
})(withAuthorization(NewChapter));
