import React from "react";
import { Row, Col, Divider, Form, Input, InputNumber } from "antd";

import Relatives from "./Relatives";
import ImageRow from "./ImageRow";
import RadioButton from "./RadioButton";

const CharacterForm = ({ form, characters, character, submit, onFailed }) => {
  const [visibility, setVisibility] = React.useState(true);
  return (
    <Form
      layout="vertical"
      scrollToFirstError
      onFinishFailed={onFailed}
      onFinish={submit}
      form={form}
      initialValues={{ firstname: "" }}
    >
      <ImageRow name="image" form={form} itemImage={character?.image} />
      <Divider />
      <Row align="bottom" gutter={[24, 24]}>
        <Col md={9} xs={24} sm={24}>
          <Form.Item
            label="Firstname"
            name="firstname"
            rules={[
              {
                required: true,
                message: "A firstname is required",
              },
              { max: 20 },
            ]}
          >
            <Input />
          </Form.Item>
        </Col>
        <Col md={9} xs={24} sm={24}>
          <Form.Item label="Lastname" name="lastname" rules={[{ max: 65 }]}>
            <Input />
          </Form.Item>
        </Col>
        <Col md={3} xs={24} sm={24}>
          <Form.Item label="Age" name="age">
            <InputNumber min={0} />
          </Form.Item>
        </Col>
        <RadioButton
          lg={3}
          md={3}
          sm={24}
          xs={24}
          name={"public"}
          label="Visibility"
          num1="1"
          num2="2"
          label1="Public"
          label2="Private"
          setFunc={setVisibility}
          val={visibility}
        />
      </Row>
      <Form.Item
        label="Description"
        name="description"
        rules={[
          {
            max: 180,
            message: "Your description cannot exceed 180 characters",
          },
        ]}
      >
        <Input.TextArea
          autoSize={{ minRows: 5 }}
          showCount={true}
          maxLength={180}
        ></Input.TextArea>
      </Form.Item>
      <Divider />
      <Relatives form={form} characters={characters} />
      <button type="submit">Create</button>
    </Form>
  );
};

export default CharacterForm;
