import React from "react";
import { connect } from "react-redux";
import Link from "next/link";
import { Row, Col, Divider } from "antd";

import { getHomeStories } from "../redux/actions/storiesActions";
import { getPopularCharacters } from "../redux/actions/charactersActions";
import { getPopularUsers } from "../redux/actions/userActions";

import { CharacterGrid, MiniGrid } from "../components/common/Grid";
import LoadingScreen from "../components/hoc/LoadingScreen";

const Home = (props) => {
  const { loading, stories, characters, users } = props;

  React.useEffect(() => {
    props.getHomeStories();
    props.getPopularCharacters();
    props.getPopularUsers();
  }, []);

  console.log(users);

  return (
    <LoadingScreen loading={loading}>
      <div className="home-page">
        <h3 className="side-heading">Popular Stories</h3>
        <div className="featured-stories">
          <Row>
            {stories.map((story, i) => (
              <Col lg={6} md={12} sm={24} xs={24}>
                <div className="featured-story" key={story.id}>
                  <Link href={`/story/${story.id}`}>
                    <a>
                      <Row>
                        <Col
                          span={24}
                          lg={{ order: i % 2 === 0 ? 1 : 2 }}
                          md={{ order: i % 2 === 0 ? 1 : 2 }}
                          sm={{ order: 1 }}
                        >
                          <img
                            src={story.banner}
                            alt={story.title}
                            data-aos={i % 2 === 0 ? "fade-up" : "fade-down"}
                          />
                        </Col>
                        <Col
                          span={24}
                          lg={{ order: i % 2 === 0 ? 2 : 1 }}
                          md={{ order: i % 2 === 0 ? 2 : 1 }}
                          sm={{ order: 2 }}
                        >
                          <div
                            className="story-details"
                            data-aos={i % 2 === 0 ? "fade-down" : "fade-up"}
                          >
                            <div className="cat-block">
                              <span className="tag">{story.category}</span>
                            </div>
                            <h2>{story.title}</h2>
                            <p className="summary">
                              {story?.summary?.length > 150
                                ? `${story.summary.slice(0, 150)}...`
                                : story.summary}
                              {!story.summary && "No summary yet"}
                            </p>
                            <div className="count">
                              <span>
                                <p className="number">{story.chaptersCount} </p>{" "}
                                <ion-icon name="book"></ion-icon>
                              </span>
                              <span>
                                <p className="number">{story.likesCount} </p>{" "}
                                <ion-icon name="heart"></ion-icon>
                              </span>
                              <span>
                                <p className="number">{story.commentsCount} </p>{" "}
                                <ion-icon name="chatbubble"></ion-icon>
                              </span>
                            </div>
                          </div>
                        </Col>
                      </Row>
                    </a>
                  </Link>
                </div>
              </Col>
            ))}
          </Row>
        </div>
        <Divider />
        <h3 className="side-heading">Popular Characters</h3>
        <CharacterGrid
          type="story"
          characters={characters}
          gutter={"16px"}
          columnsCountBreakPoints={{
            1600: 6,
            1200: 4,
            900: 4,
            750: 2,
            600: 2,
            350: 1,
          }}
        />
      </div>
    </LoadingScreen>
  );
};

const mapStateToProps = (state) => ({
  stories: state.stories.homeStories,
  characters: state.characters.popularCharacters,
  users: state.auth.popularUsers,
  loading: state.stories.loading,
});

export default connect(mapStateToProps, {
  getHomeStories,
  getPopularCharacters,
  getPopularUsers,
})(Home);
