import { Comment, Tooltip, Avatar } from "antd";
import moment from "moment";

import { dummy } from "../../utils/dummy";

const Comments = ({ comments }) => {
  return (
    <section className="comments">
      <h3>{comments.length} Comments</h3>
      {comments.map((comment) => (
        <Comment
          actions={[<span key="comment-basic-reply-to">Reply </span>]}
          author={<a>{comment.username}</a>}
          avatar={<Avatar src={comment.userImage} alt={comment.username} />}
          content={<p>{comment.content}</p>}
          datetime={
            <Tooltip
              title={
                typeof comment.createdAt === "object"
                  ? moment
                      .unix(comment.createdAt.seconds)
                      .format("YYYY-MM-DD HH:mm:ss")
                  : moment(comment.createdAt).format("YYYY-MM-DD HH:mm:ss")
              }
            >
              <span>
                {typeof comment.createdAt === "object"
                  ? moment.unix(comment.createdAt.seconds).fromNow()
                  : moment(comment.createdAt).fromNow()}
              </span>
            </Tooltip>
          }
        />
      ))}
      <Comment
        actions={[<span key="comment-basic-reply-to">Reply </span>]}
        author={<a>Han Solo</a>}
        avatar={<Avatar src={dummy.person} alt={dummy.name} />}
        content={
          <p>
            We supply a series of design principles, practical patterns and high
            quality design resources (Sketch and Axure), to help people create
            their product prototypes beautifully and efficiently.
          </p>
        }
        datetime={
          <Tooltip title={moment().format("YYYY-MM-DD HH:mm:ss")}>
            <span>{moment().fromNow()}</span>
          </Tooltip>
        }
      />
    </section>
  );
};

export default Comments;
