import React from "react";
import { connect } from "react-redux";
import { Row, Col } from "antd";

import { getHomeStories } from "../redux/actions/storiesActions";

import { MiniGrid } from "../components/common/Grid";

const Home = (props) => {
  const { loading, stories } = props;

  React.useEffect(() => {
    props.getHomeStories();
  }, []);

  console.log(stories);
  return (
    <div className="home-page">
      <h2>Featured Stories</h2>
      {/* <div className="grid-wrapper">
        <MiniGrid
          stories={stories}
          gutter={"20px"}
          type="mini"
          columnsCountBreakPoints={{ 1600: 4, 1200: 4, 900: 2, 350: 1 }}
        />
      </div> */}
      <div className="featured-stories">
        {stories.map((story, i) => (
          <div className="featured-story" key={story.id}>
            <Row>
              <Col span={24} order={i % 2 === 0 ? 1 : 2}>
                <img
                  src={story.banner}
                  alt={story.title}
                  data-aos={i % 2 === 0 ? "fade-up" : "fade-down"}
                />
              </Col>
              <Col span={24} order={i % 2 === 0 ? 2 : 1}>
                <div
                  className="story-details"
                  data-aos={i % 2 === 0 ? "fade-down" : "fade-up"}
                >
                  <h2>{story.title}</h2>
                  <p className="summary">{story.summary}</p>
                  <div className="count">
                    <span>
                      <p className="number">{story.chaptersCount} </p>{" "}
                      <ion-icon name="book"></ion-icon>
                    </span>
                    <span>
                      <p className="number">{story.likesCount} </p>{" "}
                      <ion-icon name="heart"></ion-icon>
                    </span>
                    <span>
                      <p className="number">{story.commentsCount} </p>{" "}
                      <ion-icon name="chatbubble"></ion-icon>
                    </span>
                  </div>
                </div>
              </Col>
            </Row>
          </div>
        ))}
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({
  stories: state.stories.homeStories,
  loading: state.stories.loading,
});

export default connect(mapStateToProps, { getHomeStories })(Home);
