import React from "react";
import { Form, Row, Col, InputNumber, Input } from "antd";

const ChapterForm = ({ form, submit }) => {
  return (
    <Form
      layout="vertical"
      form={form}
      onFinish={submit}
      initialValues={{
        number: 0,
        title: "",
        body: "",
        status: "published",
        locations: [],
        characters: [],
      }}
    >
      <Row gutter={[24, 24]}>
        <Col lg={20} md={20} sm={24} xs={24}>
          <Form.Item
            rules={[
              {
                required: true,
                message: "A title is required",
              },
            ]}
            name="title"
            label="Title"
          >
            <Input />
          </Form.Item>
        </Col>
        <Col lg={4} md={4} sm={24} xs={24}>
          <Form.Item
            rules={[
              {
                required: true,
                message: "A number is required",
              },
            ]}
            label="Number"
            name="number"
          >
            <InputNumber />
          </Form.Item>
        </Col>
      </Row>
    </Form>
  );
};

export default ChapterForm;
