import { Form, Input, Tooltip } from "antd";

const LinkComp = ({ name, label, info, setInfo }) => {
  //console.log(object);
  const validate_url = (url, name) => {
    if (
      /^(https?:\/\/)?((w{3}\.)?)${name}\.com\/(#!\/)?[a-z0-9_]+$/i.test(url)
    ) {
      return true;
    } else if (!url) {
      setInfo({
        ...info,
        [name]: {
          ...info[name],
          error: ``,
        },
      });
    } else
      setInfo({
        ...info,
        [name]: {
          ...info[name],
          error: `The link is not a valid ${name} url`,
        },
      });
  };
  return (
    <Input
      addonBefore={
        <Tooltip title={label}>
          <ion-icon name={`logo-${name}`}></ion-icon>
        </Tooltip>
      }
      addonAfter={!info[name].errors && <ion-icon name="checkmark"></ion-icon>}
      onChange={(e) => {
        setInfo({
          ...info,
          [name]: { ...info[name], value: e.target.value },
        });
        validate_url(info[name], name);
      }}
      value={info[name].value}
    />
  );
};

export default LinkComp;
