import { Col, Space, Form, Radio } from "antd";

const RadioButon = ({
  lg,
  sm,
  xs,
  md,
  name,
  label,
  num1,
  num2,
  label1,
  label2,
  form,
}) => {
  return (
    <Col lg={lg} md={md} sm={sm} xs={xs}>
      <Form.Item name={name} label={label}>
        <div className="radio">
          <Radio.Group>
            <Space size={32}>
              <div className="radio__1">
                <input
                  value={true}
                  checked={form.getFieldValue(name)}
                  id={`radio-${num1}`}
                  type="radio"
                />
                <label htmlFor={`radio-${num1}`}></label>
                <p>{label1}</p>
              </div>
              <div className="radio__2">
                <input
                  value={false}
                  checked={!form.getFieldValue(name)}
                  id={`radio-${num2}`}
                  type="radio"
                />
                <label htmlFor={`radio-${num2}`}></label>
                <p>{label2}</p>
              </div>
            </Space>
          </Radio.Group>
        </div>
      </Form.Item>
    </Col>
  );
};

export default RadioButon;
