import { Input, Select } from "antd";
import React from "react";

const Search = ({ router }) => {
  const [type, setType] = React.useState("stories");
  const [loading, setLoading] = React.useState(false);

  const submitSearch = (search) => {
    setLoading(true);
    router.push(`/search/${type}/${search}`);
    setLoading(false);
  };

  return (
    <div className="search">
      <Input.Search loading={loading} enterButton onSearch={submitSearch} />
      <Select value={type} onChange={(e) => setType(e.target.value)}>
        <Select.Option value="stories">Stories</Select.Option>
        <Select.Option value="characters">Characters</Select.Option>
        <Select.Option value="users">Users</Select.Option>
      </Select>
    </div>
  );
};

export default Search;
