import React from "react";
import { Form, Col, Row, Select } from "antd";

const LocationForm = ({ form, stories }) => {
  return (
    <Form layout="vertical" form={form}>
      <Row gutter={[24, 24]}>
        <Col md={16} sm={24} xs={24}>
          <Form.Item name="name" label="Name">
            <Input />
          </Form.Item>
        </Col>
        <Col md={8} sm={24} xs={24}>
          <Form.Item name="storyId" label="Story">
            <Select>
              {stories?.map((story) => (
                <Select.Option key={story.id} value={story.id}>
                  {story.title}
                </Select.Option>
              ))}
            </Select>
          </Form.Item>
        </Col>
      </Row>
    </Form>
  );
};

export default LocationForm;
