import React from "react";
import { connect } from "react-redux";
import { Form } from "antd";
import { useRouter } from "next/router";

import { useAuth } from "../../../../../hooks/userHooks";
import {
  getStory,
  getChapter,
  editChapter,
  getUserLocations,
} from "../../../../../redux/actions/storiesActions";
import { getUserCharacters } from "../../../../../redux/actions/charactersActions";

import ChapterForm from "../../../../../components/forms/ChapterForm";
import LoadingScreen from "../../../../../components/hoc/LoadingScreen";
import RedirectComp from "../../../../../components/hoc/RedirectComp";

const EditChapter = (props) => {
  const [form] = Form.useForm();
  const {
    characters,
    loading,
    storyExists,
    story,
    locations,
    loadingChapter,
  } = props;
  const auth = useAuth();
  const router = useRouter();
  const [body, setBody] = React.useState("");

  React.useEffect(() => {
    props.getStory(router.query.id);
    props.getChapter(router.query.chapid);
  }, []);

  React.useEffect(() => {
    if (auth.user) {
      props.getUserCharacters(auth.user.uid);
      props.getUserLocations(auth.user.uid);
    }
  }, [auth]);

  const submit = (values) => {
    delete values.currentChar;
    delete values.currentLoc;

    props.addChapter({
      ...values,
      authorName: auth.user.username,
      storyId: story.id,
      body,
      status: values.status === "true",
    });
  };
  console.log(chapter);

  return (
    <LoadingScreen loading={loading}>
      <RedirectComp type="404" condition={storyExists}>
        <RedirectComp
          type="403"
          condition={auth.user && auth.user.uid === story.authorId}
        >
          <div className="custom-form">
            <div className="inner">
              <h2 className="side-heading">Add a new chapter</h2>
              <ChapterForm
                submit={submit}
                form={form}
                characters={characters}
                locations={locations}
                body={body}
                setBody={setBody}
                type="edit"
                loadingChapter={loadingChapter}
                loading={loading}
                chapter={chapter}
              />
            </div>
          </div>
        </RedirectComp>
      </RedirectComp>
    </LoadingScreen>
  );
};

const mapStateToProps = (state) => ({
  chapter: state.stories.chapter,
  story: state.stories.story,
  characters: state.characters.userCharacters,
  storyExists: state.stories.storyExists,
  loading: state.stories.loading,
  locations: state.stories.userLocations,
  loadingChapter: state.stories.loadingChapter,
});

export default connect(mapStateToProps, {
  getStory,
  getChapter,
  getUserCharacters,
  getUserLocations,
  editChapter,
})(EditChapter);
