import { Row, Col } from "antd";
import Link from "next/link";

import MenuBloc from "./MenuBloc";
import { CATEGORIES } from "../../utils/constants";

const FullMenu = ({ menuVisible, toggleMenu }) => {
  return (
    <div className={`fullscreen-menu ${menuVisible ? "visible" : ""}`}>
      <div className="close-btn custom-btn" onClick={toggleMenu}>
        <ion-icon name="close"></ion-icon>
      </div>
      <Row wrap={true}>
        <Col md={14}>
          <nav className="menu-nav">
            <MenuBloc title="Home" link="/" icon="home" />
            <MenuBloc title="Profile" link="/profile" icon="person" />
            <MenuBloc title="Browse" link="/browse" icon="folder-open" />
            <MenuBloc title="Resume reading" link="/" icon="book" />
            <MenuBloc title="Home" link="/" icon="home" />
          </nav>
        </Col>
        <Col md={10}>
          <div className="cat-bloc">
            <h2>Categories</h2>
            <nav className="cat-nav">
              <ul>
                {CATEGORIES.map((cat) => (
                  <li>
                    <Link href={`/category/${cat.value}`}>
                      <a>{cat.name}</a>
                    </Link>
                  </li>
                ))}
              </ul>
            </nav>
          </div>
        </Col>
      </Row>
    </div>
  );
};

export default FullMenu;
