import {
  Comment,
  Tooltip,
  Avatar,
  Input,
  Divider,
  Button,
  Row,
  Col,
  Popconfirm,
} from "antd";
import Link from "next/link";
import moment from "moment";
import Report from "../common/Report";

const Comments = ({
  comments,
  auth,
  commentBody,
  setCommentBody,
  submit,
  responseBody,
  setResponseBody,
  deleteComment,
  chapid,
}) => {
  const [toggleCommentForm, setToggleCommentForm] = React.useState(false);
  const [toggleAnswerForm, setToggleAnswerForm] = React.useState(false);
  const [clickedForm, setClickedForm] = React.useState("");

  const topComments = comments.filter((comm) => !comm.answer);
  const responses = comments.filter((comm) => comm.answer);
  const allComments = topComments.map((comm) => {
    let res = [];
    responses.forEach((response) => {
      if (response.commentAnsweredId === comm.id) {
        res.push(response);
      }
    });
    return { ...comm, responses: res };
  });

  const commentToggle = (id) => {
    setClickedForm(id);
    setToggleCommentForm(!toggleCommentForm);
  };

  const answerToggle = (id) => {
    setClickedForm(id);
    setToggleAnswerForm(!toggleAnswerForm);
  };

  const deleteFunc = (id) => {
    deleteComment(id, chapid);
  };

  return (
    <section className="comments">
      {auth.user ? (
        <>
          <h3>Add a comment</h3>
          <Row>
            <Col md={24}>
              <Input.TextArea
                value={commentBody}
                onChange={(e) => setCommentBody(e.target.value)}
                autoSize={{ minRows: 5 }}
                showCount={true}
                maxLength={800}
              ></Input.TextArea>
            </Col>
            <Col md={24}>
              <Button onClick={submit} type="primary">
                Comment
              </Button>
            </Col>
          </Row>
          <Divider />
        </>
      ) : (
        <p style={{ marginBottom: "20px" }}>
          You need to be logged in to post a comment.{" "}
          <Link href={"/auth"}>
            <a>Click here to login or create an account !</a>
          </Link>
        </p>
      )}
      <h3>{comments.length} Comments</h3>
      {allComments.map((comment) => (
        <>
          <Comment
            key={comment.id}
            actions={[
              auth && auth.user && (
                <span
                  key="comment-basic-reply-to"
                  onClick={commentToggle.bind(null, comment.id)}
                >
                  Reply{" "}
                </span>
              ),
              comment.userId === auth.user.uid && (
                <Popconfirm
                  title="Do you want to delete this comment ?"
                  okText="Yes"
                  cancelText="No"
                >
                  <span onClick={deleteFunc.bind(null, comment.id)}>
                    Delete
                  </span>
                </Popconfirm>
              ),
              auth.user && auth.user.uid !== comment.userId && (
                <Report type="comment" data={comment} />
              ),
            ]}
            author={
              <Link href={`/profile/${comment.userId}`}>
                <a>{comment.username}</a>
              </Link>
            }
            avatar={
              <Link href={`/profile/${comment.userId}`}>
                <a>
                  <Avatar src={comment.userImage} alt={comment.username} />
                </a>
              </Link>
            }
            content={<p>{comment.content}</p>}
            datetime={
              <Tooltip
                title={
                  comment.createdAt && typeof comment.createdAt === "object"
                    ? moment
                        .unix(comment.createdAt.seconds)
                        .format("YYYY-MM-DD HH:mm:ss")
                    : moment(comment.createdAt).format("YYYY-MM-DD HH:mm:ss")
                }
              >
                <span>
                  {comment.createdAt && typeof comment.createdAt === "object"
                    ? moment.unix(comment.createdAt.seconds).fromNow()
                    : moment(comment.createdAt).fromNow()}
                </span>
              </Tooltip>
            }
          >
            {comment.responses.map((c) => (
              <>
                <Comment
                  key={c.id}
                  actions={[
                    auth && auth.user && (
                      <span
                        key="comment-basic-reply-to"
                        onClick={answerToggle.bind(null, c.id)}
                      >
                        Reply{" "}
                      </span>
                    ),
                    comment.userId === auth.user.uid && (
                      <span onClick={deleteFunc.bind(null, c.id)}>Delete</span>
                    ),
                    auth.user &&
                      (auth.user.uid !== comment.userId)(
                        <Report type="comment" data={comment} />
                      ),
                  ]}
                  author={
                    <Link href={`/profile/${c.userId}`}>
                      <a>{c.username}</a>
                    </Link>
                  }
                  avatar={
                    <Link href={`/profile/${c.userId}`}>
                      <a>
                        <Avatar src={c.userImage} alt={c.username} />
                      </a>
                    </Link>
                  }
                  content={<p>{c.content}</p>}
                  datetime={
                    <Tooltip
                      title={
                        comment.createdAt && typeof c.createdAt === "object"
                          ? moment
                              .unix(c.createdAt.seconds)
                              .format("YYYY-MM-DD HH:mm:ss")
                          : moment(c.createdAt).format("YYYY-MM-DD HH:mm:ss")
                      }
                    >
                      <span>
                        {comment.createdAt && typeof c.createdAt === "object"
                          ? moment.unix(c.createdAt.seconds).fromNow()
                          : moment(c.createdAt).fromNow()}
                      </span>
                    </Tooltip>
                  }
                />
                {clickedForm === c.id && toggleAnswerForm && (
                  <>
                    <Row>
                      <Col md={24}>
                        <Input.TextArea
                          value={responseBody}
                          onChange={(e) => setResponseBody(e.target.value)}
                          autoSize={{ minRows: 5 }}
                          showCount={true}
                          maxLength={800}
                          placeholder={`@${c.username}`}
                        ></Input.TextArea>
                      </Col>
                      <Col md={24}>
                        <Button onClick={submit} type="primary">
                          Comment
                        </Button>
                      </Col>
                    </Row>
                    <Divider />
                  </>
                )}
              </>
            ))}
          </Comment>

          {clickedForm === comment.id && toggleCommentForm ? (
            <>
              <Row>
                <Col md={24}>
                  <Input.TextArea
                    value={responseBody}
                    onChange={(e) => setResponseBody(e.target.value)}
                    autoSize={{ minRows: 5 }}
                    showCount={true}
                    maxLength={800}
                    placeholder={`@${comment.username}`}
                  ></Input.TextArea>
                </Col>
                <Col md={24}>
                  <Button onClick={submit} type="primary">
                    Comment
                  </Button>
                </Col>
              </Row>
              <Divider />
            </>
          ) : null}
        </>
      ))}
    </section>
  );
};

export default Comments;
