import React from "react";

import { addStoryToFavorite } from "../../redux/actions/storiesActions";

const Like = ({ id, title, type, isFavorite }) => {
  console.log(isFavorite);
  const likeItem = () => {
    if (type === "story") {
      addStoryToFavorite(id, isFavorite, title);
    }
  };

  return (
    <div
      className={`btn-follow ${isFavorite ? "liked" : ""} icon custom-icon`}
      onClick={likeItem}
    >
      <ion-icon name="heart"></ion-icon>
    </div>
  );
};

export default Like;
