import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { Row, Modal, Button } from "antd";

import Banner from "../../components/profile/Banner";
import Tabs from "../../components/profile/Tabs";
import Stories from "../../components/profile/Stories";
import Characters from "../../components/profile/Characters";
import Followers from "../../components/profile/Followers";
import Locations from "../../components/profile/Locations";
import Settings from "../../components/profile/Settings";
import Loader from "../../components/common/Loader";

import { getProfile } from "../../redux/actions/userActions";
import { getUserStories } from "../../redux/actions/storiesActions";
import { getUserCharacters } from "../../redux/actions/charactersActions";
import { getFavoriteAuthors } from "../../redux/actions/userActions";
import { useAuth } from "../../hooks/userHooks";

const Profile = ({
  getProfile,
  profile,
  stories,
  getUserStories,
  getUserCharacters,
  getFavoriteAuthors,
  characters,
}) => {
  const [tabs] = useState([
    "stories",
    "characters",
    "locations",
    "favorites stories",
    "favorites characters",
  ]);
  const [modalVisible, setModalVisible] = useState(false);
  const [settings, openSettings] = useState(false);
  const [currentTab, setCurrentTab] = useState("stories");
  const changeTab = (tab) => setCurrentTab(tab);

  const auth = useAuth();

  useEffect(() => {
    if (auth.user) {
      getProfile();
      getUserStories();
      getUserCharacters();
      getFavoriteAuthors();
    }
  }, [auth]);

  return (
    <div className="profile">
      {!auth.isLoading && auth.user ? (
        <>
          <Banner
            profile={profile}
            openSettings={openSettings}
            setModalVisible={setModalVisible}
          />
          <section className="profile-content">
            <Tabs currentTab={currentTab} tabs={tabs} changeTab={changeTab} />
            {currentTab === "stories" && <Stories stories={stories} />}
            {currentTab === "characters" && (
              <Characters characters={characters} user={auth.user} />
            )}
            {currentTab === "locations" && <Locations />}
            {currentTab === "favorites stories" && (
              <Stories stories={stories} />
            )}
            {currentTab === "favorites characters" && (
              <Characters characters={characters} user={auth.user} />
            )}
          </section>
          <Modal
            title="128 Followers"
            width={"50%"}
            visible={modalVisible}
            onOk={() => setModalVisible(false)}
            onCancel={() => setModalVisible(false)}
            footer={[
              <Button key="back" onClick={() => setModalVisible(false)}>
                OK
              </Button>,
            ]}
          >
            <Row>
              <Followers />
            </Row>
          </Modal>
          <Modal
            title="Settings"
            visible={settings}
            onOk={() => openSettings(false)}
            onCancel={() => openSettings(false)}
            footer={[
              <Button
                key="submit"
                type="primary"
                onClick={() => openSettings(false)}
              >
                Submit
              </Button>,
            ]}
          >
            <Settings />
          </Modal>
        </>
      ) : auth.isLoading ? (
        <Loader />
      ) : (
        <p>Oula</p>
      )}
    </div>
  );
};

const mapStateToProps = (state, ownProps) => ({
  profile: state.auth.profile,
  stories: state.stories.userStories,
  characters: state.characters.userCharacters,
});

export default connect(mapStateToProps, {
  getProfile,
  getUserStories,
  getUserCharacters,
  getFavoriteAuthors,
})(Profile);
