import React from "react";
import { useRouter } from "next/router";
import { Row, Col, message } from "antd";
import { connect } from "react-redux";

import {
  getStory,
  getChapters,
  getStoryCharacters,
  deleteChapter,
  isStoryFavorite,
  addStoryToFavorite,
  removeStoryFromFavorite,
} from "../../../redux/actions/storiesActions";
import { useAuth } from "../../../hooks/userHooks";

import CharacterCard from "../../../components/common/CharacterCard";
import LocationCard from "../../../components/common/LocationCard";
import WithLink from "../../../components/hoc/WithLink";
import RedirectComp from "../../../components/hoc/RedirectComp";
import LoadingScreen from "../../../components/hoc/LoadingScreen";
import Chapters from "../../../components/story/Chapters";
import Banner from "../../../components/story/Banner";
import { CharacterGrid } from "../../../components/common/Grid";

const Story = ({
  getStory,
  story,
  loading,
  getChapters,
  chapters,
  getStoryCharacters,
  deleteChapter,
  actionMessage,
  isStoryFavorite,
  isFavorite,
  addStoryToFavorite,
  storyExists,
  loadingFav,
  removeStoryFromFavorite,
}) => {
  const router = useRouter();
  const auth = useAuth();

  React.useEffect(() => {
    getStory(router.query.id);
    getChapters(router.query.id);
    getStoryCharacters(router.query.id);
    if (!auth.isLoading) {
      isStoryFavorite(router.query.id);
    }
  }, [router.query.id, auth]);

  React.useEffect(() => {
    if (actionMessage) {
      message.success(actionMessage);
      getChapters(router.query.id);
    }
  }, [actionMessage]);

  const deleteChapterFunc = (id) => {
    deleteChapter(id, router.query.id);
  };

  return (
    <LoadingScreen loading={loading}>
      <LoadingScreen loading={loadingFav}>
        <RedirectComp type="404" condition={storyExists}>
          <RedirectComp type="403" condition={story.public}>
            <div className="story">
              <Row gutter={24}>
                <Banner
                  loading={loading}
                  story={story}
                  id={router.query.id}
                  isFavorite={isFavorite}
                  addStoryToFavorite={addStoryToFavorite}
                  removeStoryFromFavorite={removeStoryFromFavorite}
                  auth={auth}
                />
                <Col sm={24} md={16} lg={18}>
                  <div className="story-chapters">
                    <Chapters
                      chapters={chapters}
                      id={router.query.id}
                      deleteChapter={deleteChapterFunc}
                    />
                    <h3 className="heading">Main Characters</h3>
                    <CharacterGrid
                      gutter={[24, 16]}
                      xxl={4}
                      xl={4}
                      lg={4}
                      md={6}
                      sm={8}
                      xs={12}
                      characters={story.mainCharacters}
                      type="show"
                    />
                    <Row gutter={[24, 16]}>
                      {story.mainCharacters?.map((c) => (
                        <Col key={c.id} xs={12} sm={8} md={6} lg={4}>
                          <WithLink link={`/character/${c.id}`}>
                            <CharacterCard character={c} type="story" />
                          </WithLink>
                        </Col>
                      ))}
                    </Row>
                    <h3 className="heading">Secondary Characters</h3>
                    <Row gutter={[24, 16]}>
                      {story.secondaryCharacters?.map((c) => (
                        <Col key={c.id} xs={12} sm={8} md={6} lg={4}>
                          <WithLink link={`/character/${c.id}`}>
                            <CharacterCard character={c} type="story" />
                          </WithLink>
                        </Col>
                      ))}
                    </Row>
                    <h3 className="heading">Locations</h3>
                    <Row gutter={[24, 16]}>
                      <Col xs={12} sm={8} md={6} lg={4}>
                        <LocationCard />
                      </Col>
                      <Col xs={12} sm={8} md={6} lg={4}>
                        <LocationCard />
                      </Col>
                      <Col xs={12} sm={8} md={6} lg={4}>
                        <LocationCard />
                      </Col>
                      <Col xs={12} sm={8} md={6} lg={4}>
                        <LocationCard />
                      </Col>
                      <Col xs={12} sm={8} md={6} lg={4}>
                        <LocationCard />
                      </Col>
                      <Col xs={12} sm={8} md={6} lg={4}>
                        <LocationCard />
                      </Col>
                    </Row>
                  </div>
                </Col>
              </Row>
            </div>
          </RedirectComp>
        </RedirectComp>
      </LoadingScreen>
    </LoadingScreen>
  );
};

const mapStateToProps = (state) => ({
  story: state.stories.story,
  loading: state.stories.loading,
  chapters: state.stories.chapters,
  actionMessage: state.stories.message,
  loadingChapter: state.stories.loadingChapter,
  isFavorite: state.stories.isFavorite,
  storyExists: state.stories.storyExists,
  loadingFav: state.stories.loadingFav,
});

export default connect(mapStateToProps, {
  getStory,
  getChapters,
  getStoryCharacters,
  deleteChapter,
  isStoryFavorite,
  addStoryToFavorite,
  removeStoryFromFavorite,
})(Story);
