import React from "react";

const Like = ({
  id,
  title,
  type,
  isFavorite,
  likeFunc,
  username,
  removeFunc,
  authorId,
}) => {
  const likeItem = () => {
    if (type === "story" && !isFavorite) {
      likeFunc(id, username, title, authorId);
    }
    if (type === "story" && isFavorite) {
      removeFunc(id, title);
    }
    if (type === "character" && !isFavorite) {
      likeFunc(id, username, title, authorId);
    }
    if (type === "character" && isFavorite) {
      removeFunc(id, title);
    }
  };
  return (
    <div
      className={`btn-follow ${isFavorite ? "liked" : ""} icon custom-icon`}
      onClick={likeItem}
    >
      <ion-icon name="heart"></ion-icon>
    </div>
  );
};

export default Like;
