import React from "react";
import Link from "next/link";

import { Row, Col, Image, Tag } from "antd";
import { dummy } from "../../../utils/dummy";

const Story = () => {
  return (
    <div className="story">
      <Row gutter={24}>
        <Col sm={24} lg={6}>
          <div className="story-details">
            <div className="poster">
              <Image
                src={dummy.poster}
                alt="poster"
                width={"100%"}
                height={"100%"}
              />
            </div>
            <h2>Becoming</h2>
            <p className="author">
              by{" "}
              <Link href="/profile/1">
                <a>Hawoly Ba</a>
              </Link>
            </p>
            <h3>Summary</h3>
            <p className="summary">
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Neque
              numquam, quibusdam debitis laborum quia rem nihil, deserunt, dolor
              aut repellendus illum doloremque consectetur dolorum id
              necessitatibus perspiciatis iste sint porro.
            </p>
            <h3>Tags</h3>
            <div className="tags">
              <Tag color="#f50">#f50</Tag>
            </div>
          </div>
        </Col>
        <Col sm={24} lg={18}>
          <div className="story-chapters"></div>
        </Col>
      </Row>
    </div>
  );
};

export default Story;
