import {
  Comment,
  Tooltip,
  Avatar,
  Input,
  Divider,
  Button,
  Row,
  Col,
} from "antd";
import Link from "next/link";
import moment from "moment";

const Comments = ({ comments, auth, commentBody, setCommentBody }) => {
  const topComments = comments.filter((comm) => !comm.answer);
  const responses = comments.filter((comm) => comm.answer);
  const allComments = topComments.map((comm) => {
    let res = [];
    responses.forEach((response) => {
      if (response.commentAnsweredId === comm.id) {
        res.push(response);
      }
    });
    return { ...comm, responses: res };
  });

  return (
    <section className="comments">
      {auth.user ? (
        <>
          <h3>Add a comment</h3>
          <Row>
            <Col md={24}>
              <Input.TextArea
                value={commentBody}
                onChange={(e) => setCommentBody(e.target.value)}
                autoSize={{ minRows: 5 }}
                showCount={true}
                maxLength={800}
              ></Input.TextArea>
            </Col>
            <Col md={24}>
              <Button type="primary">Comment</Button>
            </Col>
          </Row>
          <Divider />
        </>
      ) : (
        <p style={{ marginBottom: "20px" }}>
          You need to be logged in to post a comment.{" "}
          <Link href={"/auth"}>
            <a>Click here to login or create an account !</a>
          </Link>
        </p>
      )}
      <h3>{comments.length} Comments</h3>
      {allComments.map((comment) => (
        <Comment
          key={comment.id}
          actions={[<span key="comment-basic-reply-to">Reply </span>]}
          author={
            <Link href={`/profile/${comment.userId}`}>
              <a>{comment.username}</a>
            </Link>
          }
          avatar={
            <Link href={`/profile/${comment.userId}`}>
              <a>
                <Avatar src={comment.userImage} alt={comment.username} />
              </a>
            </Link>
          }
          content={<p>{comment.content}</p>}
          datetime={
            <Tooltip
              title={
                typeof comment.createdAt === "object"
                  ? moment
                      .unix(comment.createdAt.seconds)
                      .format("YYYY-MM-DD HH:mm:ss")
                  : moment(comment.createdAt).format("YYYY-MM-DD HH:mm:ss")
              }
            >
              <span>
                {typeof comment.createdAt === "object"
                  ? moment.unix(comment.createdAt.seconds).fromNow()
                  : moment(comment.createdAt).fromNow()}
              </span>
            </Tooltip>
          }
        >
          {comment.responses.map((c) => (
            <Comment
              key={c.id}
              actions={[<span key="comment-basic-reply-to">Reply </span>]}
              author={<a>{c.username}</a>}
              avatar={<Avatar src={c.userImage} alt={c.username} />}
              content={<p>{c.content}</p>}
              datetime={
                <Tooltip
                  title={
                    typeof c.createdAt === "object"
                      ? moment
                          .unix(c.createdAt.seconds)
                          .format("YYYY-MM-DD HH:mm:ss")
                      : moment(c.createdAt).format("YYYY-MM-DD HH:mm:ss")
                  }
                >
                  <span>
                    {typeof c.createdAt === "object"
                      ? moment.unix(c.createdAt.seconds).fromNow()
                      : moment(c.createdAt).fromNow()}
                  </span>
                </Tooltip>
              }
            />
          ))}
        </Comment>
      ))}
    </section>
  );
};

export default Comments;
