import React from "react";
import { Form, Tabs, Input, Row, Col, Modal, Button } from "antd";
import {
  LockOutlined,
  UserOutlined,
  LinkOutlined,
  FileImageOutlined,
} from "@ant-design/icons";

import LinkComp from "./LinkComp";

const { TabPane } = Tabs;

const Settings = ({ profile, openSettings, setOpenSettings }) => {
  const [form] = Form.useForm();
  const [info, setInfo] = React.useState({});

  React.useEffect(() => {
    if (profile) {
      setInfo({
        username: profile.username,
        instagram: profile.instagram,
        facebook: profile.facebook,
        twitter: profile.twitter,
        deviantart: profile.deviantart,
        image: profile.image,
      });
    }
  }, [profile]);

  const submit = (values) => console.log(values);
  console.log(info);
  return (
    <>
      <Modal
        title="Settings"
        visible={openSettings}
        onOk={() => console.log(form.getFieldsValue())}
        onCancel={() => openSettings(false)}
        footer={[
          <Button key="submit" type="primary">
            Submit changes
          </Button>,
        ]}
      >
        <div className="settings">
          <Form
            form={form}
            onFinish={submit}
            layout="vertical"
            initialValues={{}}
          >
            <Tabs defaultActiveKey="1">
              <TabPane
                tab={
                  <span>
                    <UserOutlined />
                    Username
                  </span>
                }
                key="1"
              >
                <Input
                  addonBefore={"Username"}
                  onChange={(e) =>
                    setInfo({ ...info, username: e.target.value })
                  }
                  value={info.username}
                />
              </TabPane>
              <TabPane
                tab={
                  <span>
                    <LockOutlined />
                    Password
                  </span>
                }
                key="2"
              >
                <Form.Item label="Actual password" name="password">
                  <Input.Password />
                </Form.Item>
                <Form.Item label="New password" name="confirm-password">
                  <Input.Password />
                </Form.Item>
              </TabPane>
              <TabPane
                tab={
                  <span>
                    <LinkOutlined />
                    Links
                  </span>
                }
                key="3"
              >
                <LinkComp
                  setInfo={setInfo}
                  info={info}
                  name={"facebook"}
                  label={"Facebook"}
                />
                <LinkComp
                  setInfo={setInfo}
                  info={info}
                  name={"twitter"}
                  label={"Twitter"}
                />
                <LinkComp
                  setInfo={setInfo}
                  info={info}
                  name={"instagram"}
                  label={"Instagram"}
                />
                <LinkComp
                  setInfo={setInfo}
                  info={info}
                  name={"deviantart"}
                  label={"Deviantart"}
                />
              </TabPane>
              <TabPane
                tab={
                  <span>
                    <FileImageOutlined />
                    Avatar
                  </span>
                }
                key="4"
              ></TabPane>
            </Tabs>
          </Form>
        </div>
      </Modal>
    </>
  );
};

export default Settings;
