import React from "react";
import { connect } from "react-redux";
import { useRouter } from "next/router";
import { useAuth } from "../../hooks/userHooks";
import { getStory } from "../../redux/actions/storiesActions";

const withAuthorization = (WrappedComponent) => {
  const CheckAuthorization = (props) => {
    const auth = useAuth();
    const router = useRouter();
    React.useEffect(() => {
      props.getStory(router.query.id);
    }, []);
    return !props.story.authorId || auth.isLoading ? (
      <div className="loader-container">
        <div className="loader">
          <div></div>
          <div></div>
          <div></div>
        </div>
      </div>
    ) : !auth.isLoading && !auth.user ? (
      <Result
        status="403"
        title="403"
        subTitle="Sorry, you are not authorized to access this page."
        extra={<Button type="primary">Back Home</Button>}
      />
    ) : props.story.authorId === auth.user.uid ? (
      <WrappedComponent story={props.storyProps} auth={auth} {...props} />
    ) : (
      <Result
        status="403"
        title="403"
        subTitle="Sorry, you are not authorized to access this page."
        extra={<Button type="primary">Back Home</Button>}
      />
    );
  };

  const mapStateToProps = (state) => ({ storyProps: state.stories.story });

  return connect(mapStateToProps, { getStory })(CheckAuthorization);
};

export default withAuthorization;
