import React from "react";
import { Form, Select, Row, Col, Divider } from "antd";

import Pagination from "../common/Pagination";
import LoadingScreen from "../hoc/LoadingScreen";

const Stories = ({ stories, loading, type }) => {
  const [data, setData] = React.useState([]);

  React.useEffect(() => {
    setData(stories);
  }, [stories]);

  const sortArray = (value) => {
    let newArr = [];
    if (value === "date-asc") {
      newArr = [...stories].sort((a, b) => {
        return typeof a.createdAt === "object"
          ? new Date(a.createdAt.seconds * 1000) -
              new Date(b.createdAt.seconds * 1000)
          : new Date(a.createdAt) - new Date(b.createdAt);
      });
    } else if (value === "date-desc") {
      newArr = [...stories].sort((a, b) => {
        return typeof a.createdAt === "object"
          ? new Date(b.createdAt.seconds * 1000) -
              new Date(a.createdAt.seconds * 1000)
          : new Date(b.createdAt) - new Date(a.createdAt);
      });
    } else if (value === "name-desc") {
      newArr = [...stories].sort((a, b) => {
        const keyA = a.title.toLowerCase(),
          keyB = b.title.toLowerCase();

        if (keyA > keyB) return -1;
        if (keyA < keyB) return 1;
        return 0;
      });
    } else if (value === "name-asc") {
      newArr = [...stories].sort((a, b) => {
        const keyA = a.title.toLowerCase(),
          keyB = b.title.toLowerCase();
        if (keyA < keyB) return -1;
        if (keyA > keyB) return 1;
        return 0;
      });
    } else {
      newArr = stories;
    }
    setData(newArr);
  };

  return (
    <div className="profile-stories">
      <LoadingScreen loading={loading}>
        <Row align="center" gutter={[0, 16]}>
          <Col
            lg={{ span: 6, offset: 0 }}
            md={{ span: 4, offset: 0 }}
            sm={{ span: 6, offset: 4 }}
            xs={{ span: 6, offset: 4 }}
          >
            <h3 className="side-heading">{stories.length} Stories</h3>
          </Col>
          <Col
            lg={{ span: 6, offset: 12 }}
            md={{ span: 8, offset: 12 }}
            sm={{ span: 24, offset: 0 }}
            xs={{ span: 24, offset: 0 }}
          >
            <Form initialValues={{ sort: "date-desc" }}>
              <Form.Item label="Sort by" name="sort">
                <Select onChange={(val) => sortArray(val)}>
                  <Select.Option value="date-asc">
                    Date (ascending)
                  </Select.Option>
                  <Select.Option value="date-desc">
                    Date (descending)
                  </Select.Option>
                  <Select.Option value="name-asc">
                    Name (ascending)
                  </Select.Option>
                  <Select.Option value="name-desc">
                    Name (descending)
                  </Select.Option>
                </Select>
              </Form.Item>
            </Form>
          </Col>
        </Row>
        <Divider />
        <Pagination
          data={data}
          type={type}
          gutter={"16px"}
          type={type}
          itemType="stories"
          columnsCountBreakPoints={{ 350: 1, 750: 2, 900: 3, 1200: 4 }}
        />
      </LoadingScreen>
    </div>
  );
};

export default Stories;
