import React from "react";
import {
  Pagination as Paginate,
  Divider,
  PageHeader,
  Select,
  Form,
} from "antd";

import { StoriesGrid } from "./Grid";

const Pagination = ({
  data,
  xs,
  sm,
  lg,
  xl,
  md,
  type,
  gutter,
  columnsCountBreakPoints,
  itemType,
}) => {
  const [pagination, setPagination] = React.useState({
    currentPage: 1,
    datasPerPage: 12,
  });
  const { currentPage, datasPerPage } = pagination;
  const indexOfLastData = currentPage * datasPerPage;
  const indexOfFirstData = indexOfLastData - datasPerPage;
  const currentDatas = data.slice(indexOfFirstData, indexOfLastData);

  const [sortValue, setSortValue] = React.useState("date-asc");

  const handleClick = (e) =>
    setPagination({ ...pagination, currentPage: Number(e) });

  const renderDatas =
    itemType === "stories" ? (
      <StoriesGrid
        gutter={gutter}
        type={type}
        stories={currentDatas}
        columnsCountBreakPoints={columnsCountBreakPoints}
      />
    ) : null;

  let pageNumbers = [];
  for (let i = 1; i <= Math.ceil(data.length / datasPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <React.Fragment>
      <div className="ctrl-section">
        <Paginate
          size="small"
          showQuickJumper
          onChange={handleClick}
          total={data.length}
          pageSize={datasPerPage}
          showTotal={(total, range) =>
            `${range[0]}-${range[1]} of ${total} items`
          }
        />
        <Form>
          <Form.Item label="Sort by">
            <Select value={sortValue}>
              <Select.Option value="date-asc">by date(ascending)</Select.Option>
              <Select.Option value="date-desc">
                by date(descending)
              </Select.Option>
              <Select.Option value="name-asc">
                by name (ascending)
              </Select.Option>
              <Select.Option value="name-desc">
                by name (descending)
              </Select.Option>
            </Select>
          </Form.Item>
        </Form>
      </div>
      <div></div>
      <Divider />
      {renderDatas}
      <Divider />
      <Paginate
        size="small"
        showQuickJumper
        onChange={handleClick}
        total={data.length}
        pageSize={datasPerPage}
        showTotal={(total, range) =>
          `${range[0]}-${range[1]} of ${total} items`
        }
      />
    </React.Fragment>
  );
};

export default Pagination;
