import React from "react";
import { connect } from "react-redux";
import { Form, Divider, Row, Col } from "antd";
import { createEditor } from "slate";
import { Slate, Editable, withReact } from "slate-react";
import { useRouter } from "next/router";

import { useAuth } from "../../../../hooks/userHooks";
import { getStory } from "../../../../redux/actions/storiesActions";
import { getUserCharacters } from "../../../../redux/actions/charactersActions";

import ChapterForm from "../../../../components/forms/ChapterForm";
import CharacterSelector from "../../../../components/forms/CharacterSelector";
import LoadingScreen from "../../../../components/hoc/LoadingScreen";
import RedirectComp from "../../../../components/hoc/RedirectComp";

const NewChapter = (props) => {
  const [form] = Form.useForm();
  const { characters, loading, storyExists, story } = props;
  const editor = React.useMemo(() => withReact(createEditor()), []);
  const auth = useAuth();
  const router = useRouter();

  React.useEffect(() => {
    props.getStory(router.query.id);
  }, []);

  React.useEffect(() => {
    if (auth.user) {
      props.getUserCharacters(auth.user.uid);
    }
  }, [auth]);

  const [value, setValue] = React.useState([
    {
      type: "paragraph",
      children: [{ text: "A line of text in a paragraph." }],
    },
  ]);

  const submit = (values) => {
    console.log(values);
  };

  return (
    <LoadingScreen loading={loading}>
      <RedirectComp
        type="403"
        condition={!auth.isloading && auth.user.uid === story.authorId}
      >
        <div className="custom-form">
          <div className="inner">
            <h2 className="side-heading">Add a new chapter</h2>
            <ChapterForm submit={submit} form={form} />
            {/* <Slate
              editor={editor}
              value={value}
              onChange={(newValue) => setValue(newValue)}
            >
              <Editable
                onKeyDown={(event) => {
                  if (event.key === "&") {
                    event.preventDefault();
                    // editor.insertText("and");
                  }
                }}
              />
            </Slate> */}
            <Divider />
            <Row gutter={24}>
              <Col md={12} xs={24} sm={24}>
                <CharacterSelector
                  label={"Characters in this chapter"}
                  arrInForm={"characters"}
                  form={form}
                  type="chapter"
                  characters={characters}
                  lg1={24}
                />
              </Col>
            </Row>
          </div>
        </div>
      </RedirectComp>
    </LoadingScreen>
  );
};

const mapStateToProps = (state) => ({
  story: state.stories.story,
  characters: state.characters.userCharacters,
  storyExists: state.stories.storyExists,
  loading: state.stories.loading,
});

export default connect(mapStateToProps, { getStory, getUserCharacters })(
  NewChapter
);
