import React from "react";
import { Row, Col, Space, Divider, Form, Spin, message } from "antd";
import { connect } from "react-redux";
import { useRouter } from "next/router";

import {
  CATEGORIES,
  COPYRIGHTS,
  LANGUAGES,
  STATUS,
} from "../../utils/constants";
import { useAuth } from "../../hooks/userHooks";
import { getUserCharacters } from "../../redux/actions/charactersActions";
import { addStory } from "../../redux/actions/storiesActions";

import InputGroup from "../../components/common/Input";
import SelectGroup from "../../components/common/Select";
import Tags from "../../components/forms/Tags";
import UploadImage from "../../components/forms/UploadImage";
import Loader from "../../components/common/Loader";

const NewStory = () => {
  const auth = useAuth();
  const [form] = Form.useForm();
  const router = useRouter();

  const submit = (values) => {
    console.log(values);
  };

  return (
    <div className="new-story custom-form">
      <div className="inner">
        <h2 className="side-heading">Add a new story</h2>
        <Form onFinish={submit} form={form} scrollToFirstError>
          <Row>
            <Form.Item
              name="title"
              rule={[{ required: true, message: "A title is required" }]}
              label="Title"
              value={form.getFieldValue("title")}
            >
              <div className="input-group">
                <input className="form-input" type="text" id="title" />
              </div>
            </Form.Item>
          </Row>
          <Divider />
          <button htmlType="submit">Create</button>
        </Form>
      </div>
    </div>
  );
};

export default NewStory;
