import React from "react";
import { useRouter } from "next/router";
import { Form, message } from "antd";
import { connect } from "react-redux";

import { useAuth } from "../../../hooks/userHooks";
import {
  editCharacter,
  getCharacter,
  getUserCharacters,
} from "../../../redux/actions/charactersActions";

import LoadingScreen from "../../../components/hoc/LoadingScreen";
import RedirectComp from "../../../components/hoc/RedirectComp";
import CharacterForm from "../../../components/forms/CharacterForm";

const EditCharacter = (props) => {
  const {
    character,
    characters,
    characterId,
    loadingCharacter,
    loading,
    charaExists,
  } = props;
  const auth = useAuth();
  const [form] = Form.useForm();
  const router = useRouter();

  const [visibility, setVisibility] = React.useState(true);

  React.useEffect(() => {
    if (auth.user) {
      props.getCharacter(router.query.id);
      props.getUserCharacters(auth.user.uid);
    }
  }, [auth]);

  React.useEffect(() => {
    form.setFieldsValue({ public: visibility });
  }, [visibility]);

  const onFailed = () => {
    window.scrollTo(0, 0);
    message.error("There are some errors");
  };

  const submit = (values) => {
    // props.addCharacter({
    //   ...values,
    //   authorId: auth.user.uid,
    //   authorName: auth.user.username,
    // });
    console.log(values);
  };

  return (
    <LoadingScreen loading={loading}>
      <RedirectComp type="404" condition={charaExists}>
        <RedirectComp
          type="403"
          condition={auth.user && auth.user.uid === character.authorId}
        >
          <div className="custom-form new-character">
            <div className="inner">
              <h2 className="side-heading">
                Edit character:
                {` ${character.firstname} ${
                  character.lastname && character.lastname
                }`}
              </h2>
              <CharacterForm
                submit={submit}
                onFailed={onFailed}
                form={form}
                character={character}
                characters={characters}
                visibility={visibility}
                setVisibility={setVisibility}
                loadingCharacter={loadingCharacter}
                type="edit"
                initialValues={{ ...character }}
              />
            </div>
          </div>
        </RedirectComp>
      </RedirectComp>
    </LoadingScreen>
    // <LoadingScreen loading={auth.isLoading}>
    //   <RedirectComp condition={auth.user} type="redirect">
    //     <div className="new-character custom-form">
    //       <div className="inner">
    //         <h2 className="side-heading">Add a new character</h2>
    //         <CharacterForm
    //           submit={submit}
    //           onFailed={onFailed}
    //           form={form}
    //           characters={characters}
    //           visibility={visibility}
    //           setVisibility={setVisibility}
    //           loadingCharacter={loadingCharacter}
    //           type="add"
    //         />
    //       </div>
    //     </div>
    //   </RedirectComp>
    // </LoadingScreen>
  );
};

const mapStateToProps = (state) => ({
  character: state.characters.character,
  characters: state.characters.userCharacters,
  loading: state.characters.loading,
  loadingCharacter: state.characters.loadingCharacter,
  characterId: state.characters.characterId,
  charaExists: state.characters.charaExists,
});

export default connect(mapStateToProps, {
  getCharacter,
  editCharacter,
  getUserCharacters,
})(EditCharacter);
