import React from "react";
import { Form, Select } from "antd";

const SortInput = ({ data }) => {
  const [form] = Form.useForm();
  const [datas, setDatas] = React.useState(data);
  React.useEffect(() => {
    if (form.getFieldsValue("sort") === "date-asc") {
      setDatas(
        datas.sort((a, b) => {
          return typeof a.createdAt === "object"
            ? new Date(a.createdAt.seconds * 1000) -
                new Date(b.createdAt.seconds * 1000)
            : new Date(a.createdAt) - new Date(b.createdAt);
        })
      );
    } else if (form.getFieldsValue("sort" === "name-desc")) {
      setDatas(
        datas.sort((a, b) => {
          const keyA = a.title,
            keyB = b.title;
          if (keyA < keyB) return -1;
          if (keyA > keyB) return 1;
          return 0;
        })
      );
    } else if (form.getFieldsValue("sort" === "name-asc")) {
      setDatas(
        datas.sort((a, b) => {
          const keyA = a.title,
            keyB = b.title;
          if (keyA > keyB) return -1;
          if (keyA < keyB) return 1;
          return 0;
        })
      );
    }
  }, [form.getFieldsValue("sort")]);
  return (
    <Form>
      <Form.Item label="Sort by" name="sort">
        <Select>
          <Select.Option value="date-asc">by date(ascending)</Select.Option>
          <Select.Option value="date-desc">by date(descending)</Select.Option>
          <Select.Option value="name-asc">by name (ascending)</Select.Option>
          <Select.Option value="name-desc">by name (descending)</Select.Option>
        </Select>
      </Form.Item>
    </Form>
  );
};

export default SortInput;
