import React from "react";
import { Form, Tabs, Input, Modal, Upload, message } from "antd";
import {
  LockOutlined,
  UserOutlined,
  LinkOutlined,
  FileImageOutlined,
  LoadingOutlined,
  PlusOutlined,
  StarOutlined,
} from "@ant-design/icons";

import LinkComp from "./LinkComp";

const { TabPane } = Tabs;

function getBase64(img, callback) {
  const reader = new FileReader();
  reader.addEventListener("load", () => callback(reader.result));
  reader.readAsDataURL(img);
}

function beforeUpload(file) {
  const isJpgOrPng = file.type === "image/jpeg" || file.type === "image/png";
  if (!isJpgOrPng) {
    message.error("You can only upload JPG/PNG file!");
  }
  const isLt2M = file.size / 1024 / 1024 < 2;
  if (!isLt2M) {
    message.error("Image must smaller than 2MB!");
  }
  return isJpgOrPng && isLt2M;
}

const Settings = ({
  profile,
  openSettings,
  setOpenSettings,
  changeProfile,
}) => {
  const [form] = Form.useForm();
  const [info, setInfo] = React.useState({
    username: "",
    deviantart: "",
    facebook: "",
    instagram: "",
    twitter: "",
    actualPassword: "",
    newPassword: "",
    confirmPassword: "",
    errors: {
      actual: "",
      confirm: "",
      instagram: "",
      deviantart: "",
      facebook: "",
      twitter: "",
      username: "",
    },
  });
  const [imageUrl, setImageUrl] = React.useState("");
  const [loading, setLoading] = React.useState(false);

  const handleChange = (imageInfo) => {
    if (imageInfo.file.status === "uploading") {
      setLoading(true);
      return;
    }
    if (imageInfo.file.status === "done") {
      setInfo({ ...info, image: imageInfo.file.originFileObj });
      getBase64(imageInfo.file.originFileObj, (imageUrl) => {
        setLoading(false);

        setImageUrl(imageUrl);
      });
    }
  };

  const uploadButton = (
    <div>
      {loading ? <LoadingOutlined /> : <PlusOutlined />}
      <div style={{ marginTop: 8 }}>Upload</div>
    </div>
  );

  React.useEffect(() => {
    if (profile) {
      setImageUrl(profile.image);
      setInfo({
        ...info,
        username: profile.username,
        instagram: profile.instagram,
        facebook: profile.facebook,
        twitter: profile.twitter,
        deviantart: profile.deviantart,
        image: profile.image,
      });
    }
  }, [profile]);

  React.useEffect(() => {
    if (info.newPassword && info.confirmPassword) {
      if (info.newPassword !== info.confirmPassword) {
        console.log(info);
        setInfo({ ...info, mario: "yoshi" });
        setInfo({
          ...info,
          errors: {
            ...info.errors,
            confirm: "The passwords are not matching",
          },
        });
      } else {
        setInfo({
          ...info,
          errors: {
            ...info.errors,
            confirm: "",
          },
        });
      }
      if (!info.actualPassword) {
        setInfo({
          ...info,
          errors: {
            ...info.errors,
            actual: "The actual password is required",
          },
        });
      } else {
        setInfo({
          ...info,
          errors: {
            ...info.errors,
            actual: "",
          },
        });
      }
    }
  }, [info.newPassword, info.confirmPassword, info.actualPassword]);

  React.useEffect(() => {
    if (info.username.length > 0 && info.username.length < 4) {
      setInfo({
        ...info,
        errors: {
          ...info.errors,
          username: "Your username needs to be at least 4 characters long",
        },
      });
    } else if (info.username.length > 15) {
      setInfo({
        ...info,
        errors: {
          ...info.errors,
          username: "Your username cannot exceed 15 characters",
        },
      });
    } else {
      setInfo({
        ...info,
        errors: {
          ...info.errors,
          username: "",
        },
      });
    }
  }, [info.username]);

  const submit = () => {
    let errors = [];

    for (let i in info.errors) {
      if (info.errors[i]) errors.push(info.errors[i]);
    }

    if (errors.length === 0) {
      changeProfile(
        {
          username: info.username,
          twitter: info.twitter,
          instagram: info.instagram,
          deviantart: info.deviantart,
          facebook: info.facebook,
          newPassword: info.newPassword,
          actualPassword: info.actualPassword,
          image: info.image,
        },
        setOpenSettings,
        setInfo,
        info
      );
    } else {
      message.error("There are some errors in the settings");
    }
  };

  const closeModal = () => {
    setImageUrl(profile.image);
    setInfo({
      ...info,
      actualPassword: "",
      newPassword: "",
      confirmPassword: "",
      errors: {
        actual: "",
        confirm: "",
        instagram: "",
        deviantart: "",
        facebook: "",
        twitter: "",
        username: "",
      },
      username: profile.username,
      instagram: profile.instagram,
      facebook: profile.facebook,
      twitter: profile.twitter,
      deviantart: profile.deviantart,
      image: profile.image,
    });
    setOpenSettings(false);
  };

  console.log(info.errors);
  return (
    <>
      <Modal
        title="Settings"
        visible={openSettings}
        onOk={submit}
        okText="Submit changes"
        onCancel={closeModal}
      >
        <div className="settings">
          <Tabs defaultActiveKey="1">
            <TabPane
              tab={
                <span>
                  <UserOutlined />
                  Username
                </span>
              }
              key="1"
            >
              <Input
                maxLength={15}
                addonBefore={"Username"}
                onChange={(e) => setInfo({ ...info, username: e.target.value })}
                value={info.username}
              />
              {info.errors.username && (
                <div className="ant-form-item-explain ant-form-item-explain-error">
                  <div role="alert">{info.errors.username}</div>
                </div>
              )}
            </TabPane>
            <TabPane
              tab={
                <span>
                  <LockOutlined />
                  Password
                </span>
              }
              key="2"
            >
              <div className="form-group">
                <label htmlFor="password">Actual Password</label>
                <div className="input-group">
                  <Input.Password
                    onChange={(e) =>
                      setInfo({
                        ...info,
                        actualPassword: e.target.value,
                      })
                    }
                  />
                  <div className="input-group__icon">
                    <ion-icon name="key"></ion-icon>
                  </div>
                </div>
                {info.errors.actual && (
                  <div className="ant-form-item-explain ant-form-item-explain-error">
                    <div role="alert">{info.errors.actual}</div>
                  </div>
                )}
              </div>
              <div className="form-group">
                <label htmlFor="password">New Password</label>
                <div className="input-group">
                  <Input.Password
                    onChange={(e) =>
                      setInfo({
                        ...info,
                        newPassword: e.target.value,
                      })
                    }
                  />
                  <div className="input-group__icon">
                    <ion-icon name="key"></ion-icon>
                  </div>
                </div>
              </div>
              <div className="form-group">
                <label htmlFor="password">Confirm New Password</label>
                <div className="input-group">
                  <Input.Password
                    onChange={(e) =>
                      setInfo({
                        ...info,
                        confirmPassword: e.target.value,
                      })
                    }
                  />
                  <div className="input-group__icon">
                    <ion-icon name="key"></ion-icon>
                  </div>
                </div>
                {info.errors.confirm && (
                  <div className="ant-form-item-explain ant-form-item-explain-error">
                    <div role="alert">{info.errors.confirm}</div>
                  </div>
                )}
              </div>
            </TabPane>
            <TabPane
              tab={
                <span>
                  <LinkOutlined />
                  Links
                </span>
              }
              key="3"
            >
              <LinkComp
                setInfo={setInfo}
                info={info}
                name={"facebook"}
                label={"Facebook"}
              />
              <div style={{ margin: "10px 0" }}></div>
              <LinkComp
                setInfo={setInfo}
                info={info}
                name={"twitter"}
                label={"Twitter"}
              />
              <div style={{ margin: "10px 0" }}></div>
              <LinkComp
                setInfo={setInfo}
                info={info}
                name={"instagram"}
                label={"Instagram"}
              />
              <div style={{ margin: "10px 0" }}></div>
              <LinkComp
                setInfo={setInfo}
                info={info}
                name={"deviantart"}
                label={"Deviantart"}
              />
              <div style={{ margin: "10px 0" }}></div>
            </TabPane>
            <TabPane
              tab={
                <span>
                  <FileImageOutlined />
                  Avatar
                </span>
              }
              key="4"
            >
              <Upload
                showUploadList={{
                  showRemoveIcon: true,
                  removeIcon: <StarOutlined />,
                }}
                listType="picture-card"
                className="cover-uploader"
                showUploadList={true}
                beforeUpload={beforeUpload}
                onChange={handleChange}
                maxCount={1}
              >
                {imageUrl ? (
                  <img
                    src={imageUrl}
                    alt="image"
                    style={{
                      width: "100%",
                      height: "100%",
                      objectFit: "cover",
                    }}
                  />
                ) : (
                  uploadButton
                )}
              </Upload>
            </TabPane>
          </Tabs>
        </div>
      </Modal>
    </>
  );
};

export default Settings;
