import React from "react";
import { Form, Row, Col, InputNumber, Input, Divider } from "antd";

import ItemSelector from "./ItemSelector";
import CustomEditor from "./CustomEditor";

const ChapterForm = ({ form, submit, characters, locations }) => {
  console.log(form.getFieldsValue());
  return (
    <Form
      layout="vertical"
      form={form}
      onFinish={submit}
      initialValues={{
        number: 0,
        title: "",
        body: "",
        status: "published",
        locations: [],
        characters: [],
      }}
    >
      <Row gutter={[24, 24]}>
        <Col lg={20} md={20} sm={24} xs={24}>
          <Form.Item
            rules={[
              {
                required: true,
                message: "A title is required",
              },
            ]}
            name="title"
            label="Title"
          >
            <Input />
          </Form.Item>
        </Col>
        <Col lg={4} md={4} sm={24} xs={24}>
          <Form.Item
            rules={[
              {
                required: true,
                message: "A number is required",
              },
            ]}
            label="Number"
            name="number"
          >
            <InputNumber min={1} />
          </Form.Item>
        </Col>
      </Row>
      <Form.Item label="Start writing your story" name="body">
        <CustomEditor form={form} />
      </Form.Item>
      <Divider />
      <Row gutter={24}>
        <Col md={12} xs={24} sm={24}>
          <ItemSelector
            label={"Characters in this chapter"}
            arrInForm={"characters"}
            form={form}
            type="chapter"
            subject={characters}
            lg1={24}
            subjectType="characters"
          />
        </Col>
        <Col md={12} xs={24} sm={24}>
          <ItemSelector
            label={"Locations in this chapter"}
            arrInForm={"locations"}
            form={form}
            type="chapter"
            subject={locations}
            lg1={24}
            subjectType="locations"
          />
        </Col>
      </Row>
    </Form>
  );
};

export default ChapterForm;
