import React from "react";
import Link from "next/link";
import { connect } from "react-redux";
import { useRouter } from "next/router";
import { Row, Col, Divider, Space, PageHeader, Tooltip } from "antd";

import { dummy } from "../../../../../utils/dummy";
import { getChapter } from "../../../../../redux/actions/storiesActions";

import Comments from "../../../../../components/chapter/Comments";
import CharacterCard from "../../../../../components/common/CharacterCard";
import LocationCard from "../../../../../components/common/LocationCard";
import LoadingScreen from "../../../../../components/hoc/LoadingScreen";

const Chapter = (props) => {
  const router = useRouter();
  const { loading, chapter } = props;

  React.useEffect(() => {
    props.getChapter(router.query.id, router.query.chapid);
  }, [router.query.id, router.query.chapid]);

  return (
    <LoadingScreen loading={loading}>
      <div className="main-chapter">
        <div className="chapter-page">
          <Tooltip title="Next Chapter" placement="bottom">
            <div className="icon custom-icon nav-btn next-btn">
              <ion-icon name="chevron-forward-circle"></ion-icon>
            </div>
          </Tooltip>
          <Tooltip title="Previous Chapter" placement="bottom">
            <div className="icon custom-icon nav-btn prev-btn">
              <ion-icon name="chevron-back-circle"></ion-icon>
            </div>
          </Tooltip>
          <PageHeader
            className="site-page-header"
            onBack={() => router.push(`/story/${router.query.id}`)}
            title="Back to story"
          />
          <h2>
            {chapter.number}. {chapter.title}
          </h2>
          <div className="meta">
            <Divider orientation="left">
              <Link href="/profile/1">
                <a>
                  <Space direction="vertical" align="center">
                    <div className="avatar">
                      <img src={dummy.person} alt={dummy.name} />
                    </div>
                    <h5>{chapter.authorName}</h5>
                  </Space>
                </a>
              </Link>
            </Divider>
          </div>
          <section className="chapter-content">{chapter.body}</section>
          <Divider />
          <Comments />
        </div>
        <div className="main-chapter-details">
          {/*  <h3>Characters in this chapter</h3>
        <Row gutter={[12, 12]}>
          <Col md={12}>
            <CharacterCard />
          </Col>
          <Col md={12}>
            <CharacterCard />
          </Col>
          <Col md={12}>
            <CharacterCard />
          </Col>
          <Col md={12}>
            <CharacterCard />
          </Col>
  </Row> */}
          <Divider />
          <h3>Locations in this chapter</h3>
          {/* <Row gutter={[12, 12]}>
          <Col md={12}>
            <LocationCard />
          </Col>
          <Col md={12}>
            <LocationCard />
          </Col>
          <Col md={12}>
            <LocationCard />
          </Col>
          <Col md={12}>
            <LocationCard />
          </Col>
        </Row>*/}
        </div>
      </div>
    </LoadingScreen>
  );
};

const mapStateToProps = (state) => ({
  chapter: state.stories.chapter,
  loading: state.stories.loading,
});

export default connect(mapStateToProps, { getChapter })(Chapter);
