import { db, auth } from "../fbConfig";

import { GET_PROFILE, GET_FAVORITE_AUTHORS } from "../../utils/constants";

export const log_in = (email, password) => (dispatch) => {
  console.log(email, password);
  auth.signInWithEmailAndPassword(email, password);
};

export const getProfile = () => (dispatch) => {
  db.collection("users")
    .doc(auth.currentUser.uid)
    .onSnapshot((doc) => {
      return dispatch({
        type: GET_PROFILE,
        payload: { id: doc.id, ...doc.data() },
      });
    });
};

export const getFavoriteAuthors = () => async (dispatch) => {
  const userFav = await db
    .collection("usersLikes")
    .where("senderId", "==", auth.currentUser.uid)
    .get()
    .then((docs) => {
      let favArr = [];
      docs.forEach((doc) => {
        favArr = [...favArr, doc.data().recipient];
      });
      return favArr;
    })
    .then((users) => {
      let res = [];
      users.forEach((user) => {
        db.collection("users")
          .doc(user)
          .get()
          .then((doc) => {
            res = [...res, { id: doc.id, ...doc.data() }];
          });
      });
      return res;
    })
    .then((result) => console.log(result));
};
