import React from "react";
import { Row, Col, Spin, Pagination as Paginate, Divider } from "antd";

import { StoriesGrid } from "./Grid";

const Pagination = ({ data, loading, type }) => {
  const [pagination, setPagination] = React.useState({
    currentPage: 1,
    datasPerPage: 12,
  });
  const { currentPage, datasPerPage } = pagination;
  const indexOfLastData = currentPage * datasPerPage;
  const indexOfFirstData = indexOfLastData - datasPerPage;
  const currentDatas = data.slice(indexOfFirstData, indexOfLastData);

  const handleClick = (e) =>
    setPagination({ ...pagination, currentPage: Number(e) });

  const renderDatas =
    type === "stories" ? (
      <StoriesGrid loading={loading} type={type} stories={currentDatas} />
    ) : null;

  let pageNumbers = [];
  for (let i = 1; i <= Math.ceil(data.length / datasPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <React.Fragment>
      <Paginate
        showQuickJumper
        onChange={handleClick}
        total={data.length}
        pageSize={datasPerPage}
        showTotal={(total, range) =>
          `${range[0]}-${range[1]} of ${total} items`
        }
      />
      <Divider />
    </React.Fragment>
  );
};

export default Pagination;
