import React from "react";
import { connect } from "react-redux";
import { Row, Col, Form, Rate } from "antd";

import { onlyUnique } from "../utils/helpers";
import { getStoriesByLetter } from "../redux/actions/storiesActions";

import { StoriesGrid } from "../components/common/Grid";
import LoadingScreen from "../components/hoc/LoadingScreen";
import Filter from "../components/explore/Filter";
import AlphabetRow from "../components/explore/AlphabetRow";

const Explore = (props) => {
  const [form] = Form.useForm();
  const { stories, loading } = props;
  const [currentStories, setCurrentStories] = React.useState([]);
  const [currentLetter, setCurrentLetter] = React.useState("a");
  const [currentCategory, setCurrentCategory] = React.useState("");
  const [currentStatus, setCurrentStatus] = React.useState("");
  const [currentLanguage, setCurrentLanguage] = React.useState("");
  const [mature, setMature] = React.useState(false);
  const alphabet = [
    "a",
    "b",
    "c",
    "d",
    "e",
    "f",
    "g",
    "h",
    "i",
    "j",
    "k",
    "l",
    "m",
    "n",
    "o",
    "p",
    "q",
    "r",
    "s",
    "t",
    "u",
    "v",
    "w",
    "x",
    "y",
    "z",
  ];

  React.useEffect(() => {
    props.getStoriesByLetter(currentLetter, alphabet);
  }, [currentLetter]);

  React.useEffect(() => {
    setCurrentStories(stories.filter((story) => story.mature === mature));
  }, [stories]);

  // CATEGORY
  React.useEffect(() => {
    setCurrentStories(
      stories.filter((s) =>
        s.category
          ? s.category.includes(currentCategory)
          : s.categories.includes(currentCategory)
      )
    );
  }, [currentCategory, stories]);

  // LANGUAGE
  React.useEffect(() => {
    setCurrentStories(
      stories.filter((story) => story.language === currentLanguage)
    );
  }, [currentLanguage, stories]);

  // MATURE
  React.useEffect(() => {
    setCurrentStories(stories.filter((story) => story.mature === mature));
  }, [mature, stories]);

  // STATUS
  React.useEffect(() => {
    setCurrentStories(
      stories.filter((story) => story.status === currentStatus)
    );
  }, [currentStatus, stories]);

  console.log(mature);

  return (
    <div className="explore-page">
      <h2>Explore</h2>
      <Row gutter={[32, 24]}>
        <Filter
          form={form}
          currentCategory={currentCategory}
          setCurrentCategory={setCurrentCategory}
          currentStatus={currentStatus}
          setCurrentStatus={setCurrentStatus}
          currentLanguage={currentLanguage}
          setCurrentLanguage={setCurrentLanguage}
          mature={mature}
          setMature={setMature}
        />
        <Col span={18}>
          <AlphabetRow
            alphabet={alphabet}
            currentLetter={currentLetter}
            setCurrentLetter={setCurrentLetter}
          />
          <LoadingScreen loading={loading}>
            <StoriesGrid
              stories={currentStories}
              gutter="16px"
              columnsCountBreakPoints={{ 1600: 3, 1200: 3, 900: 3, 750: 2 }}
            />
          </LoadingScreen>
        </Col>
      </Row>
    </div>
  );
};

const mapStateToProps = (state) => ({
  stories: state.stories.storiesFromSearch,
  loading: state.stories.loading,
});

export default connect(mapStateToProps, { getStoriesByLetter })(Explore);
