import {
  GET_USER_CHARACTERS,
  GET_FAVORITE_CHARACTERS,
  ADD_CHARACTER,
} from "../../utils/constants";
import { db, auth } from "../fbConfig";

export const addCharacter = (data) => (dispatch) => {
  dispatch({ type: ADD_CHARACTER, payload: { loading: true } });
  const imageName = `${data.firstname.toLowerCase()}${data.lastname && "_"}${
    data.lastname && data.lastname.toLowerCase()
  }`;
  console.log(data);
  if (typeof data.banner === "object") {
    storage
      .ref(`${auth.currentUser.uid}/${imageName}`)
      .put(data.banner)
      .then(() => {
        return storage
          .ref(auth.currentUser.uid)
          .child(imageName)
          .getDownloadURL();
      })
      .then((url) => {
        return db.collection("stories").add({
          ...data,
          authorId: auth.currentUser.uid,
          authorName: data.authorName,
          oneShot: false,
          banner: url,
          createdAt: firebase.firestore.FieldValue.serverTimestamp(),
          likesCount: 0,
          chaptersCount: 0,
          locationsCount: 0,
          secondaryCharacters: [],
          featured: false,
          note: 0,
        });
      })
      .then((res) => {
        message.success("Story added successfully");
        setTimeout(() => {
          Router.push(`/story/${res.id}`);
          dispatch({
            type: ADD_STORY,
            payload: {
              message: "Story added successfully",
              storyId: res.id,
              loading: false,
            },
          });
        }, 1000);
      })
      .catch((err) => {
        message.error(err.message);
      });
  } else {
    db.collection("stories")
      .add({
        ...data,
        authorId: auth.currentUser.uid,
        authorName: data.authorName,
        oneShot: false,
        createdAt: firebase.firestore.FieldValue.serverTimestamp(),
        likesCount: 0,
        chaptersCount: 0,
        locationsCount: 0,
        featured: false,
        secondaryCharacters: [],
        note: 0,
      })
      .then((res) => {
        message.success("Story added successfully");
        setTimeout(() => {
          Router.push(`/story/${res.id}`);
          dispatch({
            type: ADD_STORY,
            payload: {
              message: "Story added successfully",
              storyId: res.id,
              loading: false,
            },
          });
        }, 1000);
      })
      .catch((err) => {
        message.error(err.message);
      });
  }
};

export const getUserCharacters = () => (dispatch) => {
  db.collection("characters")
    .where("authorId", "==", auth.currentUser.uid)
    .get()
    .then((docs) => {
      let items = [];
      docs.forEach((doc) => {
        items = [...items, { id: doc.id, ...doc.data() }];
      });
      return items;
    })
    .then((items) => {
      dispatch({ type: GET_USER_CHARACTERS, payload: items });
    });
};

export const getFavoriteCharacters = () => (dispatch) => {
  db.collection("charactersLikes")
    .where("senderId", "==", auth.currentUser.uid)
    .get()
    .then((docs) => {
      let favArr = [];
      docs.forEach((doc) => {
        favArr = [...favArr, doc.data().characterId];
      });
      return favArr;
    })
    .then((users) => {
      const result = users.map((user) =>
        db.collection("characters").doc(user).get()
      );
      Promise.all(result).then((res) => {
        let favUsers = [];
        res.forEach(
          (doc) => (favUsers = [...favUsers, { id: doc.id, ...doc.data() }])
        );
        dispatch({
          type: GET_FAVORITE_CHARACTERS,
          payload: favUsers,
        });
      });
    });
};
