import { Row, Col, Form, Select, Input } from "antd";

import CharacterCard, { CharacterGrid } from "../common/CharacterCard";

const Relatives = ({ characters }) => {
  const [relations, setRelations] = React.useState([]);
  const [currentChar, setCurrentChar] = React.useState("");
  const [currentRel, setCurrentRel] = React.useState("");
  const [selectedCharacters, setSelectedCharacters] = React.useState([]);

  const addRelation = () => {
    let newRelations = [...relations];
    if (currentChar && currentRel) {
      newRelations = [
        ...newRelations,
        { character: currentChar, relation: currentRel },
      ];
    }

    setRelations(newRelations);
    setSelectedCharacters([
      ...selectedCharacters,
      characters.find((c) => c.id === currentChar),
    ]);
    setCurrentChar("");
    setCurrentRel("");
  };

  console.log(setSelectedCharacters);

  return (
    <Form.Item label="Relationships">
      <Row align="bottom" gutter={[24, 24]}>
        <Col md={11} sm={24}>
          <Select onChange={(val) => setCurrentChar(val)}>
            <Option value="none" disabled>
              Select a character
            </Option>
            {characters?.map((c) => (
              <Option
                key={c.id}
                value={c.id}
              >{`${c.firstname} ${c.lastname}`}</Option>
            ))}
          </Select>
        </Col>
        <Col md={11} sm={24}>
          <Input onChange={(e) => setCurrentRel(e.target.value)} />
        </Col>
        <Col md={2} sm={24}>
          <div className="add-btn" onClick={addRelation}>
            Add
          </div>
        </Col>
      </Row>
      <Row gutter={[16, 16]}>
        {relations?.map((rel) => (
          <Col md={4} key={rel.character}>
            <CharacterCard characters={characters} />
          </Col>
        ))}
      </Row>
    </Form.Item>
  );
};

export default Relatives;
