import { Col } from "antd";

const SelectGroup = ({ md, name, options }) => {
  return (
    <Col md={md}>
      <div className="input-group">
        <label htmlFor="genre">{name}</label>
        <select className="form-input">
          {options?.map((option) => (
            <option
              style={{ textTransform: "capitalize" }}
              key={option.value}
              value={option.value}
            >
              {option.name}
            </option>
          ))}
        </select>
      </div>
    </Col>
  );
};

export default SelectGroup;
