import React from "react";
import { Form, Select } from "antd";

const SortInput = ({ form, setValue }) => {
  return (
    <Form form={form} initialValues={{ sort: "date-asc" }}>
      <Form.Item label="Sort by" name="sort">
        <Select onChange={(val) => setValue(val)}>
          <Select.Option value="date-asc">Date (ascending)</Select.Option>
          <Select.Option value="date-desc">Date (descending)</Select.Option>
          <Select.Option value="name-asc">Name (ascending)</Select.Option>
          <Select.Option value="name-desc">Name (descending)</Select.Option>
        </Select>
      </Form.Item>
    </Form>
  );
};

export default SortInput;
