import React from "react";
import Link from "next/link";
import moment from "moment";
import { Input, Modal, Comment, Tooltip, Popconfirm, Avatar } from "antd";

import { dummy } from "../../utils/dummy";
import LoadingScreen from "../hoc/LoadingScreen";

const Comments = ({ comments, auth, submit, character, loadingComments }) => {
  const [visible, setVisible] = React.useState(false);
  const [content, setContent] = React.useState("");

  const submitComment = () => {
    submit({
      userId: auth.user.uid,
      username: auth.user.username,
      content,
      characterId: character.id,
      authorId: character.authorId,
      answer: false,
    });
    setVisible(false);
    setContent("");
  };

  const closeModal = () => {
    setVisible(false);
    setContent("");
  };

  return (
    <>
      <section className="character-feedback">
        <header className="feedback-header">
          <h3 className="side-heading">Feedback</h3>
          <div className="add-comment" onClick={() => setVisible(true)}>
            <ion-icon name="add-circle"></ion-icon>
            <span>Leave a comment</span>
          </div>
        </header>
        <LoadingScreen loading={loadingComments}>
          <div className="comments-block">
            {comments &&
              comments.map((comment) => (
                <Comment
                  key={comment.id}
                  actions={[
                    // auth && auth.user && (
                    //   <span
                    //     key="comment-basic-reply-to"
                    //     onClick={commentToggle.bind(null, comment.id)}
                    //   >
                    //     Reply{" "}
                    //   </span>
                    // ),
                    comment.userId === auth.user.uid && (
                      <Popconfirm
                        title="Do you want to delete this comment ?"
                        okText="Yes"
                        cancelText="No"
                        onConfirm={() => deleteComment(comment.id, chapid)}
                      >
                        <span>Delete</span>
                      </Popconfirm>
                    ),
                    auth.user && auth.user.uid !== comment.userId && (
                      <Report type="comment" data={comment} />
                    ),
                  ]}
                  author={
                    <Link href={`/profile/${comment.userId}`}>
                      <a>{comment.username}</a>
                    </Link>
                  }
                  avatar={
                    <Link href={`/profile/${comment.userId}`}>
                      <a>
                        <Avatar
                          src={
                            comment.userImage ? comment.userImage : dummy.avatar
                          }
                          alt={comment.username}
                        />
                      </a>
                    </Link>
                  }
                  content={<p>{comment.content}</p>}
                  datetime={
                    <Tooltip
                      title={
                        comment.createdAt &&
                        typeof comment.createdAt === "object"
                          ? moment
                              .unix(comment.createdAt.seconds)
                              .format("YYYY-MM-DD HH:mm:ss")
                          : moment(comment.createdAt).format(
                              "YYYY-MM-DD HH:mm:ss"
                            )
                      }
                    >
                      <span>
                        {comment.createdAt &&
                        typeof comment.createdAt === "object"
                          ? moment.unix(comment.createdAt.seconds).fromNow()
                          : moment(comment.createdAt).fromNow()}
                      </span>
                    </Tooltip>
                  }
                />
              ))}
          </div>
        </LoadingScreen>
      </section>
      <Modal
        title={"Leave a new comment"}
        visible={visible}
        onCancel={closeModal}
        okText="Submit"
        onOk={submitComment}
      >
        <Input.TextArea
          rows={5}
          value={content}
          onChange={(e) => setContent(e.target.value)}
        ></Input.TextArea>
      </Modal>
    </>
  );
};

export default Comments;
