import { Form, Input, Tooltip } from "antd";

const LinkComp = ({ name, label, info, setInfo }) => {
  const validate_url = (url, name) => {
    if (name === "twitter") {
      if (
        /^(https?:\/\/)?((w{3}\.)?)twitter\.com\/(#!\/)?[a-z0-9_]+$/i.test(url)
      ) {
        return true;
      } else {
        setInfo({
          ...info,
          [name]: {
            ...info[name],
            errors: `The link is not a valid ${name} url`,
          },
        });
      }
    }

    // if (name === "twitter" && ) {
    //   return true;
    // } else if (name === "facebook" && /^(https?:\/\/)?((w{3}\.)?)facebook\.com\/(#!\/)?[a-z0-9_]+$/i.test(url)) {

    // return true
    // } else if (!url) {
    //   setInfo({
    //     ...info,
    //     [name]: {
    //       ...info[name],
    //       errors: ``,
    //     },
    //   });
    // } else
  };

  return (
    <Input
      addonBefore={
        <Tooltip title={label}>
          <ion-icon name={`logo-${name}`}></ion-icon>
        </Tooltip>
      }
      addonAfter={!info[name].errors && <ion-icon name="checkmark"></ion-icon>}
      onChange={(e) => {
        setInfo({
          ...info,
          [name]: { ...info[name], value: e.target.value },
        });
        //validate_url(e.target.value, name);
      }}
      value={info[name].value}
    />
  );
};

export default LinkComp;
