import React from "react";
import { Form, Select, Row, Col } from "antd";

const SortInput = ({ title, itemData, setData, type }) => {
  const sortArray = (value) => {
    let newArr = [];

    if (value === "date-asc") {
      newArr = [...itemData].sort((a, b) => {
        return typeof a.createdAt === "object"
          ? new Date(a.createdAt.seconds * 1000) -
              new Date(b.createdAt.seconds * 1000)
          : new Date(a.createdAt) - new Date(b.createdAt);
      });
    } else if (value === "date-desc") {
      newArr = [...itemData].sort((a, b) => {
        return typeof a.createdAt === "object"
          ? new Date(b.createdAt.seconds * 1000) -
              new Date(a.createdAt.seconds * 1000)
          : new Date(b.createdAt) - new Date(a.createdAt);
      });
    } else if (value === "name-desc") {
      newArr = [...itemData].sort((a, b) => {
        const keyA = a.title.toLowerCase(),
          keyB = b.title.toLowerCase();

        if (keyA > keyB) return -1;
        if (keyA < keyB) return 1;
        return 0;
      });
    } else if (value === "name-asc") {
      newArr = [...itemData].sort((a, b) => {
        const keyA = a.title.toLowerCase(),
          keyB = b.title.toLowerCase();
        if (keyA < keyB) return -1;
        if (keyA > keyB) return 1;
        return 0;
      });
    } else if (value === "firstname-asc") {
      newArr = [...itemData].sort((a, b) => {
        const keyA = a.firstname.toLowerCase(),
          keyB = b.firstname.toLowerCase();
        if (keyA < keyB) return -1;
        if (keyA > keyB) return 1;
        return 0;
      });
    } else if (value === "firstname-desc") {
      newArr = [...itemData].sort((a, b) => {
        const keyA = a.firstname.toLowerCase(),
          keyB = b.firstname.toLowerCase();
        if (keyA > keyB) return -1;
        if (keyA < keyB) return 1;
        return 0;
      });
    } else if (value === "lastname-asc") {
      newArr = [...itemData].sort((a, b) => {
        const keyA = a.lastname.toLowerCase(),
          keyB = b.lastname.toLowerCase();
        if (keyA < keyB) return -1;
        if (keyA > keyB) return 1;
        return 0;
      });
    } else if (value === "lastname-desc") {
      newArr = [...itemData].sort((a, b) => {
        const keyA = a.lastname.toLowerCase(),
          keyB = b.lastname.toLowerCase();
        if (keyA > keyB) return -1;
        if (keyA < keyB) return 1;
        return 0;
      });
    } else {
      newArr = itemData;
    }
    setData(newArr);
  };

  console.log(type);

  return (
    <Row align="center" gutter={[0, 16]}>
      <Col
        lg={{ span: 6, offset: 0 }}
        md={{ span: 4, offset: 0 }}
        sm={{ span: 6, offset: 4 }}
        xs={{ span: 18, offset: 12 }}
      >
        <h3 className="side-heading">
          {itemData.length} {title}
        </h3>
      </Col>
      <Col
        lg={{ span: 6, offset: 12 }}
        md={{ span: 8, offset: 12 }}
        sm={{ span: 24, offset: 0 }}
        xs={{ span: 24, offset: 0 }}
      >
        <Form initialValues={{ sort: "date-desc" }}>
          <Form.Item label="Sort by" name="sort">
            <Select onChange={(val) => sortArray(val)}>
              <Select.Option value="date-asc">Date (ascending)</Select.Option>
              <Select.Option value="date-desc">Date (descending)</Select.Option>
              {(type === "stories") ===
              (
                <React.Fragment>
                  <Select.Option value="name-asc">
                    Name (ascending)
                  </Select.Option>
                  <Select.Option value="name-desc">
                    Name (descending)
                  </Select.Option>
                </React.Fragment>
              )}
              {(type === "characters") ===
              (
                <React.Fragment>
                  <Select.Option value="firstname-asc">
                    Firstname (ascending)
                  </Select.Option>
                  <Select.Option value="firstname-desc">
                    Firstname (descending)
                  </Select.Option>
                  <Select.Option value="lastname-asc">
                    Lastname (ascending)
                  </Select.Option>
                  <Select.Option value="lastname-desc">
                    Lastname (descending)
                  </Select.Option>
                </React.Fragment>
              )}
            </Select>
          </Form.Item>
        </Form>
      </Col>
    </Row>
  );
};

export default SortInput;
