import { Form, Input, Tooltip, Space } from "antd";
import React from "react";

const LinkComp = ({ name, label, info, setInfo }) => {
  const validate_url = (url, name) => {
    if (name === "twitter") {
      if (
        /^(https?:\/\/)?((w{3}\.)?)twitter\.com\/(#!\/)?[a-z0-9_]+$/i.test(url)
      ) {
        setInfo({
          ...info,
          twitter: {
            ...info.twitter,
            errors: ``,
          },
        });
      } else {
        setInfo({
          ...info,
          twitter: {
            ...info.twitter,
            errors: `The link is not a valid Twitter url`,
          },
        });
      }
    } else if (name === "facebook") {
      if (
        /^(https?:\/\/)?((w{3}\.)?)facebook\.com\/(#!\/)?[a-z0-9_]+$/i.test(url)
      ) {
        setInfo({
          ...info,
          facebook: {
            ...info.facebook,
            errors: ``,
          },
        });
      } else {
        setInfo({
          ...info,
          facebook: {
            ...info.facebook,
            errors: `The link is not a valid Facebook url`,
          },
        });
      }
    } else if (name === "instagram") {
      if (
        /^(https?:\/\/)?((w{3}\.)?)instagram\.com\/(#!\/)?[a-z0-9_]+$/i.test(
          url
        )
      ) {
        return true;
      } else {
        setInfo({
          ...info,
          instagram: {
            ...info.instagram,
            errors: `The link is not a valid Instagram url`,
          },
        });
      }
    } else if (name === "deviantart") {
      if (
        /^(https?:\/\/)?((w{3}\.)?)deviantart\.com\/(#!\/)?[a-z0-9_]+$/i.test(
          url
        )
      ) {
        return true;
      } else {
        setInfo({
          ...info,
          deviantart: {
            ...info.deviantart,
            errors: `The link is not a valid Deviantart url`,
          },
        });
      }
    } else if (!url) {
      setInfo({
        ...info,
        [name]: {
          ...info[name],
          errors: ``,
        },
      });
    }
  };

  React.useEffect(() => {
    validate_url(info.facebook.value, "facebook");
  }, [info.facebook.value]);
  React.useEffect(() => {
    validate_url(info.twitter.value, "twitter");
  }, [info.twitter.value]);
  React.useEffect(() => {
    validate_url(info.instagram.value, "instagram");
  }, [info.instagram.value]);
  React.useEffect(() => {
    validate_url(info.deviantart.value, "deviantart");
  }, [info.deviantart.value]);
  console.log(info);
  return (
    <>
      <Input
        addonBefore={
          <Tooltip title={label}>
            <ion-icon name={`logo-${name}`}></ion-icon>
          </Tooltip>
        }
        addonAfter={
          !info[name].errors && <ion-icon name="checkmark"></ion-icon>
        }
        onChange={(e) => {
          setInfo({
            ...info,
            [name]: { ...info[name], value: e.target.value },
          });
        }}
        placeholder="Enter your pseudo. Ex: 'JaneDoe'"
        value={info[name].value}
      />
      {info[name].errors && (
        <div className="ant-form-item-explain ant-form-item-explain-error">
          <div role="alert">{info[name].errors}</div>
        </div>
      )}
    </>
  );
};

export default LinkComp;
