import React from "react";
import { Row, Col, Form, Input } from "antd";

const Register = ({
  email,
  password,
  confirmPassword,
  username,
  setEmail,
  setUsername,
  setPassword,
  setConfirmPassword,
  handleClick,
  signup,
  auth,
}) => {
  const [form] = Form.useForm();

  return (
    <div className="inner-auth">
      <h2>Welcome to Kronikea</h2>
      <Form
        onFinish={({ password, email, username }) =>
          auth.signup(email, password, username)
        }
        layout="vertical"
        form={form}
        initialValues={{ password: "", email: "" }}
      >
        <Row>
          <Col span={24}>
            <Form.Item
              name="email"
              label="Email Address"
              rules={[
                {
                  required: true,
                  type: "email",
                  message: "This is not valid E-mail!",
                },
              ]}
            >
              <Input />
            </Form.Item>
            {auth.errors.email && (
              <div className="ant-form-item-explain ant-form-item-explain-error">
                <div role="alert">{auth.errors.email}</div>
              </div>
            )}
          </Col>
          <Col span={24}>
            <Form.Item
              name="username"
              label="Username"
              rules={[
                {
                  required: true,
                  message: "A username is required",
                },
                {
                  max: 15,
                  type: "string",
                  message: "Your username cannot exceed 15 characters",
                },
                {
                  min: 4,
                  type: "string",
                  message:
                    "Your username needs to be at least 4 characters long",
                },
              ]}
            >
              <Input />
            </Form.Item>
            {auth.errors.username && (
              <div className="ant-form-item-explain ant-form-item-explain-error">
                <div role="alert">{auth.errors.username}</div>
              </div>
            )}
          </Col>
          <Col span={24}>
            <Form.Item
              name="password"
              label="Password"
              rules={[
                {
                  required: true,
                  message: "A password is required",
                },
                { min: 8, message: "Password must be at least 8 characters." },
              ]}
            >
              <Input.Password />
            </Form.Item>
            {auth.errors.password && (
              <div className="ant-form-item-explain ant-form-item-explain-error">
                <div role="alert">{auth.errors.password}</div>
              </div>
            )}
          </Col>
          <Col span={24}>
            <Form.Item
              name="confirmpassword"
              label="Confirm Password"
              rules={[
                {
                  required: true,
                  message: "Please confirm your password!",
                },
                ({ getFieldValue }) => ({
                  validator(_, value) {
                    if (!value || getFieldValue("password") === value) {
                      return Promise.resolve();
                    }
                    return Promise.reject(
                      new Error(
                        "The two passwords that you entered do not match!"
                      )
                    );
                  },
                }),
              ]}
            >
              <Input.Password />
            </Form.Item>
            {auth.errors.confirmPassword && (
              <div className="ant-form-item-explain ant-form-item-explain-error">
                <div role="alert">{auth.errors.confirmPassword}</div>
              </div>
            )}
          </Col>
        </Row>
        <button type="submit" className="login-btn custom-btn">
          <span>Register</span>
        </button>
      </Form>
      {/* <form className="auth-form">
        <div className="form-group">
          <label htmlFor="email">Email address</label>
          <div className="input-group">
            <input
              onChange={(e) => setEmail(e.target.value)}
              value={email}
              className="form-input"
              type="email"
              id="email"
            />
            <div className="input-group__icon">
              <ion-icon name="mail"></ion-icon>
            </div>
          </div>
        </div>
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <div className="input-group">
            <input
              onChange={(e) => setUsername(e.target.value)}
              value={username}
              className="form-input"
              type="text"
              id="username"
            />
            <div className="input-group__icon">
              <ion-icon name="person"></ion-icon>
            </div>
          </div>
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <div className="input-group">
            <input
              onChange={(e) => setPassword(e.target.value)}
              value={password}
              className="form-input"
              type="password"
              id="password"
            />
            <div className="input-group__icon">
              <ion-icon name="key"></ion-icon>
            </div>
          </div>
        </div>
        <div className="form-group">
          <label htmlFor="confirm-password">Confirm Password</label>
          <div className="input-group">
            <input
              onChange={(e) => setConfirmPassword(e.target.value)}
              value={confirmPassword}
              className="form-input"
              type="password"
              id="confirm-password"
            />
            <div className="input-group__icon">
              <ion-icon name="key"></ion-icon>
            </div>
          </div>
        </div>
        <div className="login-btn custom-btn" onClick={signup}>
          <span>Create account</span>
        </div>
      </form> */}
      <p onClick={handleClick}>Already have an account ? Login here</p>
    </div>
  );
};

export default Register;
